import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Fees.css";

const Payments = ({ user }) => {
    const navigate = useNavigate();
    const [fees, setFees] = useState([]);
    const [paymentMethod, setPaymentMethod] = useState("bank");

    useEffect(() => {
        if (!user) {
            const storedUser = JSON.parse(localStorage.getItem("user"));
            if (!storedUser) {
                navigate("/login");
                return;
            }
            fetchFees(storedUser.id);
        } else {
            fetchFees(user.id);
        }
    }, [user, navigate]);

    const fetchFees = async (studentId) => {
        try {
            const response = await fetch(`http://localhost:5000/student/fees/${studentId}`);
            if (!response.ok) throw new Error("L·ªói khi t·∫£i h·ªçc ph√≠");
            const data = await response.json();
            setFees(data);
        } catch (error) {
            console.error(error);
            setFees([]);
        }
    };

    const handlePayment = () => {
        alert(`Thanh to√°n th√†nh c√¥ng b·∫±ng ${paymentMethod === "bank" ? "Chuy·ªÉn kho·∫£n" : "Ti·ªÅn m·∫∑t"}!`);
        // üõ† TODO: G·ª≠i d·ªØ li·ªáu thanh to√°n l√™n server
    };

    return (
        <div className="fees-container">
            <h2>üí≥ Thanh to√°n h·ªçc ph√≠</h2>
            {fees.length === 0 ? (
                <p>Ch∆∞a c√≥ d·ªØ li·ªáu h·ªçc ph√≠.</p>
            ) : (
                <>
                    <table className="fees-table">
                        <thead>
                            <tr>
                                <th>üìö S·ªë m√¥n ƒëƒÉng k√Ω</th>
                                <th>üí∞ T·ªïng h·ªçc ph√≠</th>
                                <th>üìÖ Ng√†y ƒë√≥ng g·∫ßn nh·∫•t</th>
                                <th>üí≥ ƒê√£ thanh to√°n</th>
                                <th>üí∏ C√≤n l·∫°i</th>
                            </tr>
                        </thead>
                        <tbody>
                            {fees.map((fee) => (
                                <tr key={fee.id}>
                                    <td>{fee.subject_count}</td>
                                    <td>{fee.total_fee?.toLocaleString()} VNƒê</td>
                                    <td>{fee.latest_pay_at ? new Date(fee.latest_pay_at).toLocaleDateString() : "Ch∆∞a ƒë√≥ng"}</td>
                                    <td>{(fee.already_pay || 0).toLocaleString()} VNƒê</td>
                                    <td>{(fee.remaining || 0).toLocaleString()} VNƒê</td>
