import { useState, useEffect, useRef } from "react";
import StudentClasses from "./student";
import TeacherClasses from "./teacher";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { io } from "socket.io-client";
import "./Documents.css";

const socket = io("http://localhost:5000");

const Courses = ({ user }) => {
    const [selectedClass, setSelectedClass] = useState(null);
    const [announcements, setAnnouncements] = useState([]);
    const [assignments, setAssignments] = useState([]);
    const [submittedAssignments, setSubmittedAssignments] = useState([]);
    const [students, setStudents] = useState([]);
    const [notifications, setNotifications] = useState([]);
    const navigate = useNavigate();
    const [newAnnouncement, setNewAnnouncement] = useState("");
    const [selectedFile, setSelectedFile] = useState(null);
    const fileInputRef = useRef(null);
    const storedUser = localStorage.getItem("user");
    const [currentUser, setCurrentUser] = useState(user || (storedUser ? JSON.parse(storedUser) : null));

    useEffect(() => {
        if (!currentUser) {
            navigate("/login");
        } else {
            fetchNotifications(currentUser.role);
        }
    }, [currentUser, navigate]);

    useEffect(() => {
        socket.on("newNotification", (notification) => {
            setNotifications((prev) => [notification, ...prev]);
        });
        return () => socket.off("newNotification");
    }, []);

    const fetchNotifications = async (role) => {
        try {
            const response = await axios.get(`http://localhost:5000/notifications?role=${role}`);
            setNotifications(response.data);
        } catch (error) {
            console.error("Lỗi khi tải thông báo:", error.message);
        }
    };
    const handleAddAnnouncement = async () => {
        if (!newAnnouncement.trim()) {
            toast.error("⚠️ Nội dung thông báo không được để trống!");
            return;
        }

        const formData = new FormData();
        formData.append("class_id", selectedClass);
        formData.append("teacher_id", currentUser.id);
        formData.append("title", "Thông báo mới");
        formData.append("message", newAnnouncement);
        if (selectedFile) {
            formData.append("file", selectedFile);
        }

        try {
            await axios.post("http://localhost:5000/notifications", formData);
            toast.success("✅ Thông báo đã được đăng!");
            setNewAnnouncement("");
            setSelectedFile(null);
            fetchNotifications("class_" + selectedClass);
        } catch (error) {
            console.error("❌ Lỗi khi gửi thông báo:", error);
            toast.error("⚠️ Không thể đăng thông báo!");
        }
    };


    const handleSelectClass = async (class_Id) => {
        setSelectedClass(class_Id);
        try {
            const [annRes, assignRes, studentRes, submitRes] = await Promise.all([
                axios.get(`http://localhost:5000/announcements/class/${class_Id}`),
                axios.get(`http://localhost:5000/assignments/class/${class_Id}`),
                axios.get(`http://localhost:5000/students/class/${class_Id}`),
                axios.get(`http://localhost:5000/submissions/class/${class_Id}`)
            ]);
            setAnnouncements(annRes.data);
            setAssignments(assignRes.data);
            setStudents(studentRes.data);
            setSubmittedAssignments(submitRes.data);
        } catch (error) {
            console.error("❌ Lỗi khi tải dữ liệu lớp học:", error);
            toast.error("⚠️ Không thể tải dữ liệu lớp học!");
        }
    };

    return (
        <div className="courses-container">
            <h2>Khóa học của bạn</h2>

            <div className="notification-section">
                <h3>🔔 Thông báo</h3>
                <ul>
                    {notifications.length > 0 ? (
                        notifications.map((noti) => (
                            <li key={noti.id} className="notification-item">
                                <p className="font-medium">{noti.title}</p>
                                <p className="text-sm text-gray-600">{noti.message}</p>
                            </li>
                        ))
                    ) : (
                        <li className="text-center text-gray-500">Không có thông báo</li>
                    )}
                </ul>
            </div>

            {!selectedClass ? (
                <>
                    <h3>Chọn lớp học</h3>
                    {currentUser?.role === "hv" ? (
                        <StudentClasses userId={currentUser.id} onSelectClass={handleSelectClass} />
                    ) : currentUser?.role === "gv" ? (
                        <TeacherClasses userId={currentUser.id} onSelectClass={handleSelectClass} />
                    ) : (
                        <p>Đang tải dữ liệu người dùng...</p>
                    )}
                </>
            ) : (
                <div className="course-content">
                    <button onClick={() => setSelectedClass(null)}>🔙 Quay lại danh sách lớp</button>

                    <h3>📢 Thông báo lớp học</h3>
                    <ul>
                        {announcements.map((ann) => (
                            <li key={ann.id}>{ann.content}</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default Courses;
