import React from "react";
import { Rnd } from "react-rnd";

const DraggablePopup = ({ children }) => {
    return (
        <Rnd
            default={{
                x: 100,
                y: 100,
                width: 400,
                height: 300,
            }}
            bounds="window"
            enableResizing={false} // Bật nếu muốn thay đổi kích thước
            style={{
                background: "white",
                border: "1px solid black",
                padding: "10px",
                borderRadius: "8px",
            }}
        >
            {children}
        </Rnd>
    );
};

export default DraggablePopup;
