            // Ki·ªÉm tra tr√πng l·ªãch
            if (isScheduleConflict(schedule, freshAssigned)) {
                alert(`‚ö†Ô∏è Kh√¥ng th·ªÉ ƒëƒÉng k√Ω l·ªõp ${className} v√¨ tr√πng l·ªãch v·ªõi l·ªõp ƒë√£ d·∫°y.`);
                return;
            }

            // Ti·∫øn h√†nh ƒëƒÉng k√Ω
            const res = await fetch(`http://localhost:5000/teacher/classes/${classId}/assign-teacher`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ teacher_id: teacherId }),
