import { useEffect, useState } from 'react';
import './History.css';

function History() {
    const [history, setHistory] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Giả lập gọi API để lấy danh sách ca học đã dạy
        const fetchHistory = async () => {
            try {
                // Gọi API thực tế ở đây
                // const response = await fetch('/api/teacher/history');
                // const data = await response.json();

                // Dữ liệu mẫu
                const data = [
                    { id: 1, date: '2025-02-10', time: '14:00 - 16:00', group: 'Lớp A1', subject: 'Toán' },
                    { id: 2, date: '2025-02-12', time: '16:30 - 18:30', group: 'Lớp B2', subject: 'Lý' },
                    { id: 3, date: '2025-02-14', time: '19:00 - 21:00', group: 'Lớp C3', subject: 'Hóa' },
                ];

                setHistory(data);
            } catch (error) {
                console.error('Lỗi khi tải dữ liệu lịch sử:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchHistory();
    }, []);

    if (loading) {
        return <div>Đang tải dữ liệu...</div>;
    }

    return (
        <div className="history-container">
            <h2>Lịch Sử Giảng Dạy</h2>
            <table className="history-table">
                <thead>
                    <tr>
                        <th>Ngày</th>
                        <th>Giờ</th>
                        <th>Nhóm</th>
                        <th>Môn</th>
                    </tr>
                </thead>
                <tbody>
                    {history.length > 0 ? (
                        history.map((session) => (
                            <tr key={session.id}>
                                <td>{session.date}</td>
                                <td>{session.time}</td>
                                <td>{session.group}</td>
                                <td>{session.subject}</td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="4">Không có dữ liệu</td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
}

export default History;
