import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { useEffect, useState } from "react";
import Home from "./Home";
import Login from "./Login";
import Register from "./Register";
import Profile from "./Profile";
import Grades from "./Grades";
import StudentSchedule from "./StudentSchedule";
import Fees from "./Fees";
import MakeUpClasses from "./MakeUpClasses";
function App() {
  const [user, setUser] = useState(null);
  const [studentId, setStudentId] = useState(1);

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser && storedUser !== "undefined") {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login setUser={setUser} />} />
        <Route path="/" element={user ? <Home user={user} /> : <Navigate to="/login" />} />
        <Route path="/register" element={<Register />} />
        <Route path="/home" element={user ? <Home user={user} /> : <Navigate to="/login" />} />
        <Route path="/profile" element={<Profile />} />
        <Route path="/grades" element={<Grades user={user} />} />
        <Route path="/schedule/" element={<StudentSchedule />} /> {/* Thêm route lịch học */}
        <Route path="/fees" element={<Fees user={user} />} />
        <Route path="/makeup-classes" element={<MakeUpClasses studentId={studentId} />} />

      </Routes>
    </Router>
  );
}

export default App;
