import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import DraggablePopup from "./DraggablePopup"; // Import DraggablePopup
import "./Profile.css";

const Profile = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [popupMessage, setPopupMessage] = useState(""); // State for popup message
    const [isPopupOpen, setIsPopupOpen] = useState(false); // State for popup visibility

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        if (!storedUser) {
            navigate("/login");
        } else {
            setUser(storedUser);
        }
    }, [navigate]);

    // Xử lý thay đổi mật khẩu
    const handleChangePassword = async () => {
        if (newPassword !== confirmPassword) {
            alert("Mật khẩu mới không khớp!");
            return;
        }

        const storedUser = JSON.parse(localStorage.getItem("user"));
        if (!storedUser) {
            alert("Lỗi: Không tìm thấy thông tin người dùng!");
            return;
        }

        try {
            const response = await fetch("http://localhost:5000/change-password", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    userId: storedUser.id,
                    oldPassword,
                    newPassword,
                }),
            });

            const data = await response.json();
            if (response.ok) {
                alert("Đổi mật khẩu thành công!");
                closeModal();  // Đóng modal khi thành công
            } else {
                alert(data.error || "Có lỗi xảy ra!");
            }
        } catch (error) {
            console.error("Lỗi khi đổi mật khẩu:", error);
            alert("Đã xảy ra lỗi khi đổi mật khẩu.");
        }
    };

    return (
        <div className="profile-container">
            <h2>👤 Thông tin cá nhân</h2>
            <div className="profile-card">
                <p><strong>👤 Họ và tên:</strong> {user.fullName}</p>
                <p><strong>📧 Email:</strong> {user.email}</p>
                <p><strong>📞 Số điện thoại:</strong> {user.phone || "Chưa cập nhật"}</p>
                <p><strong>🎭 Vai trò:</strong> {user.role.toUpperCase()}</p>
            </div>

            <button onClick={() => setIsModalOpen(true)} className="change-password-btn">
                🔑 Đổi mật khẩu
            </button>

            {/* Đổi mật khẩu sẽ được hiển thị trong DraggablePopup */}
            {isModalOpen && (
                <DraggablePopup>
                    <div className="modal-header popup-header">
                        <h4>Đổi mật khẩu</h4>
                    </div>
                    <div className="modal-body">
                        <input type="password" placeholder="Mật khẩu cũ" />
                        <input type="password" placeholder="Mật khẩu mới" />
                        <input type="password" placeholder="Xác nhận mật khẩu mới" />
                        <button onClick={() => handlePasswordChange("oldPassword", "newPassword", "confirmPassword")}>Đổi mật khẩu</button>
                    </div>
                </DraggablePopup>
            )}

            {/* Popup for feedback */}
            {isPopupOpen && <DraggablePopup>{popupMessage}</DraggablePopup>}

            <button onClick={() => navigate("/home")} className="back-btn">⬅️ Quay lại</button>
        </div>
    );
};

export default Profile;
