import { useEffect, useState } from "react";

const ManageGroups = () => {
    const [groups, setGroups] = useState([]);

    useEffect(() => {
        fetch("http://localhost:5000/student/available-classes")
            .then((res) => res.json())
            .then((data) => {
                if (Array.isArray(data)) {
                    setGroups(groupByClass(data));
                } else {
                    console.error("Dữ liệu API không hợp lệ:", data);
                    setGroups([]); // Đảm bảo groups luôn là một mảng
                }
            })
            .catch((err) => {
                console.error("Lỗi khi lấy dữ liệu:", err);
                setGroups([]); // Xử lý lỗi bằng cách gán giá trị mặc định
            });
    }, []);

    const groupByClass = (data) => {
        return data.reduce((acc, cls) => {
            const existingGroup = acc.find((g) => g.id === cls.id);
            if (existingGroup) {
                existingGroup.schedule.push({
                    date_of_week: cls.date_of_week,
                    start_at: cls.start_at,
                    end_at: cls.end_at,
                });
            } else {
                acc.push({
                    id: cls.id,
                    name: cls.name,
                    type: cls.type,
                    subject: cls.subject,
                    grade: cls.grade,
                    remaining_students: cls.remaining_students,
                    schedule: [
                        {
                            date_of_week: cls.date_of_week,
                            start_at: cls.start_at,
                            end_at: cls.end_at,
                        },
                    ],
                });
            }
            return acc;
        }, []);
    };

    const handleEdit = (cls) => {
        console.log("Chỉnh sửa lớp:", cls);
        // Thêm logic chỉnh sửa ở đây
    };

    const handleDelete = (id) => {
        console.log("Xóa lớp có ID:", id);
        // Thêm logic xóa ở đây
    };

    return (
        <div className="p-4">
            <h1 className="text-2xl font-bold mb-4">Quản lý Nhóm Học</h1>
            <table className="w-full border-collapse border border-gray-300">
                <thead>
                    <tr className="bg-gray-200">
                        <th className="border p-2">Tên nhóm</th>
                        <th className="border p-2">Loại</th>
                        <th className="border p-2">Môn học</th>
                        <th className="border p-2">Khối lớp</th>
                        <th className="border p-2">Số chỗ trống</th>
                        <th className="border p-2">Lịch học</th>
                        <th className="border p-2">Hành động</th>
                    </tr>
                </thead>
                <tbody>
                    {Array.isArray(groups) && groups.length > 0 ? (
                        groups.map((cls) => (
                            <tr key={cls.id} className="hover:bg-gray-50">
                                <td className="border p-2">{cls.name}</td>
                                <td className="border p-2">{cls.type}</td>
                                <td className="border p-2">{cls.subject}</td>
                                <td className="border p-2">{cls.grade}</td>
                                <td className="border p-2">{cls.remaining_students} chỗ trống</td>
                                <td className="border p-2">
                                    <ul className="list-disc pl-4">
                                        {cls.schedule.length > 0 ? (
                                            cls.schedule.map((sch, index) => (
                                                <li key={index}>
                                                    {sch.date_of_week}: {sch.start_at} - {sch.end_at}
                                                </li>
                                            ))
                                        ) : (
                                            <span className="text-gray-500">Chưa có lịch học</span>
                                        )}
                                    </ul>
                                </td>
                                <td className="border p-2 text-center">
                                    <button
                                        className="px-3 py-1 bg-yellow-500 text-white rounded hover:bg-yellow-600 mr-2"
                                        onClick={() => handleEdit(cls)}
                                    >
                                        Chỉnh sửa
                                    </button>
                                    <button
                                        className="px-3 py-1 bg-red-500 text-white rounded hover:bg-red-600"
                                        onClick={() => handleDelete(cls.id)}
                                    >
                                        Xóa
                                    </button>
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="7" className="border p-4 text-center text-gray-500">
                                Không có lớp nào.
                            </td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default ManageGroups;
