import { useEffect, useState } from "react";

const ManageStudents = () => {
    const [students, setStudents] = useState([]);
    const [newStudent, setNewStudent] = useState({
        username: "",
        email: "",
        phone: "",
        fullName: "",
        password: "",
    });
    const [message, setMessage] = useState(""); // Thêm state cho thông báo

    useEffect(() => {
        fetchStudents();
    }, []);

    const fetchStudents = () => {
        fetch("http://localhost:5000/manager/students")
            .then((response) => response.json())
            .then((data) => setStudents(data))
            .catch((error) => setMessage("Lỗi khi lấy học viên!"));
    };

    const handleAddStudent = (e) => {
        e.preventDefault();
        fetch("http://localhost:5000/manager/students", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(newStudent),
        })
            .then((response) => response.json())
            .then((data) => {
                setMessage(data.message); // Hiển thị thông báo trên giao diện
                fetchStudents();
                setNewStudent({ username: "", email: "", phone: "", fullName: "", password: "" });
            })
            .catch(() => setMessage("Lỗi khi thêm học viên!"));
    };

    const handleDeleteStudent = (id) => {
        if (!window.confirm("Bạn có chắc chắn muốn xóa học viên này?")) return;

        fetch(`http://localhost:5000/manager/students/${id}`, { method: "DELETE" })
            .then((response) => response.json())
            .then((data) => {
                setMessage(data.message); // Hiển thị thông báo trên giao diện
                fetchStudents();
            })
            .catch(() => setMessage("Lỗi khi xóa học viên!"));
    };

    return (
        <div>
            <h2>Quản lý học viên</h2>

            {/* Hiển thị thông báo */}
            {message && <p style={{ color: "red", fontWeight: "bold" }}>{message}</p>}

            {/* Form thêm học viên */}
            <form onSubmit={handleAddStudent}>
                <input type="text" placeholder="Tên đăng nhập" value={newStudent.username}
                    onChange={(e) => setNewStudent({ ...newStudent, username: e.target.value })} required />
                <input type="email" placeholder="Email" value={newStudent.email}
                    onChange={(e) => setNewStudent({ ...newStudent, email: e.target.value })} required />
                <input type="text" placeholder="Số điện thoại" value={newStudent.phone}
                    onChange={(e) => setNewStudent({ ...newStudent, phone: e.target.value })} required />
                <input type="text" placeholder="Họ và tên" value={newStudent.fullName}
                    onChange={(e) => setNewStudent({ ...newStudent, fullName: e.target.value })} required />
                <input type="password" placeholder="Mật khẩu" value={newStudent.password}
                    onChange={(e) => setNewStudent({ ...newStudent, password: e.target.value })} required />
                <button type="submit">Thêm học viên</button>
            </form>

            {/* Bảng danh sách học viên */}
            <table border="1">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Tên đăng nhập</th>
                        <th>Email</th>
                        <th>Số điện thoại</th>
                        <th>Họ và tên</th>
                        <th>Ngày tạo</th>
                        <th>Hành động</th>
                    </tr>
                </thead>
                <tbody>
                    {students.map((student) => (
                        <tr key={student.id}>
                            <td>{student.id}</td>
                            <td>{student.username}</td>
                            <td>{student.email}</td>
                            <td>{student.phone}</td>
                            <td>{student.fullName}</td>
                            <td>{new Date(student.created_at).toLocaleDateString()}</td>
                            <td>
                                <button onClick={() => handleDeleteStudent(student.id)}>Xóa</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ManageStudents;
