        // K·∫øt n·ªëi v√† l·∫Øng nghe th√¥ng b√°o t·ª´ WebSocket
        socket.emit("join", { userId: storedUser.id }); // Th√¥ng b√°o user ƒë√£ k·∫øt n·ªëi

        socket.on("newNotification", (notification) => {
            setNotifications((prev) => [notification, ...prev]); // Th√™m th√¥ng b√°o m·ªõi v√†o danh s√°ch
        });

        return () => {
            socket.off("newNotification"); // Ng·∫Øt k·∫øt n·ªëi khi unmount
        };
    }, [navigate]);

    const handleLogout = () => {
        localStorage.removeItem("user");
