    };

    const sidebarItems = {
        hv: [
            { path: "/schedule", label: "L·ªãch h·ªçc", icon: <FaHome /> },
            { path: "/grades", label: "ƒêi·ªÉm s·ªë", icon: <FaBell /> },
            { path: "/fees", label: "H·ªçc ph√≠", icon: <FaCog /> },
            { path: "/makeup-classes", label: "H·ªçc b√π", icon: <FaUser /> },
        ],
        gv: [
