                                        {/* N·∫øu l√† gi√°o vi√™n, hi·ªÉn th·ªã n√∫t x√≥a */}
                                        {currentUser?.role === "gv" && (
                                            <button
                                                onClick={() => handleDeleteAnnouncement(notif.id)}
                                                className="delete-btn"
                                            >
