import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const RegisterGroup = () => {
    const navigate = useNavigate();
    const [classes, setClasses] = useState([]);
    const currentUser = JSON.parse(localStorage.getItem("user"));

    useEffect(() => {
        if (!currentUser) {
            navigate("/login");
        } else {
            fetchClasses();
        }
    }, [navigate]);

    const fetchClasses = async () => {
        try {
            const res = await axios.get("http://localhost:5000/student/classes/${userID}");
            console.log("📌 Dữ liệu từ API:", res.data); // Debug
            if (res.data.length === 0) {
                console.log("📢 Học viên đã đăng ký tất cả lớp. Không còn lớp nào để đăng ký.");
            } else {
                const groupedClasses = groupByClass(res.data);
                setClasses(groupedClasses);
            }

        } catch (err) {
            console.error("❌ Lỗi tải danh sách lớp học:", err);
        }
    };


    const handleRegister = async (classId) => {
        try {
            const response = await axios.post(
                "http://localhost:5000/student/register-group",
                { userId: currentUser.id, classId },
                { headers: { Authorization: `Bearer ${currentUser.token}` } }
            );
            alert(response.data.message);
            fetchClasses();
        } catch (error) {
            console.error("❌ Lỗi đăng ký lớp học:", error);
            alert("Đăng ký thất bại!");
        }
    };

    const groupByClass = (data) => {
        return data.map((item) => ({
            id: item.id,
            name: item.name,
            subject: item.subject,
            type: item.type,
            grade: item.grade,
            max_student: item.max_student,
            current_student: item.current_student || 0, // Lấy số học viên đã đăng ký
            remaining_students: item.max_student - (item.current_student || 0), // Tính số học viên còn lại
            schedule: item.schedule
                ? item.schedule.split("; ").map((sch) => {
                    const parts = sch.split(": "); // Tách ngày học và giờ học
                    const date = parts[0]; // Lấy ngày
                    const timeRange = parts[1]?.split(" - "); // Tách giờ bắt đầu - kết thúc
                    return {
                        date_of_week: date,
                        start_at: timeRange ? timeRange[0] : "",
                        end_at: timeRange ? timeRange[1] : "",
                    };
                })
                : [],
        }));
    };




    return (
        <div className="max-w-4xl mx-auto p-6 bg-white rounded-xl shadow-lg">
            <h2 className="text-2xl font-bold text-gray-800 mb-4">Đăng ký lớp học</h2>
            <div className="overflow-x-auto">
                <table className="w-full table-auto border-collapse border border-gray-200">
                    <thead>
                        <tr className="bg-gray-100">
                            <th className="border p-2 text-left">Tên lớp</th>
                            <th className="border p-2 text-left">Loại</th>
                            <th className="border p-2 text-left">Khối</th>
                            <th className="border p-2 text-left">Còn lại</th>
                            <th className="border p-2 text-left">Lịch học</th>
                            <th className="border p-2 text-center">Đăng ký</th>
                        </tr>
                    </thead>
                    <tbody>
                        {classes.length > 0 ? (
                            classes.map((cls) => (
                                <tr key={cls.id} className="hover:bg-gray-50">
                                    <td className="border p-2">{cls.name}</td>
                                    <td className="border p-2">{cls.type}</td>
                                    <td className="border p-2">{cls.grade}</td>
                                    <td className="border p-2">{cls.remaining_students} chỗ trống</td>
                                    <td className="border p-2">
                                        <ul className="list-disc pl-4">
                                            {cls.schedule.length > 0 ? (
                                                cls.schedule.map((sch, index) => (
                                                    <li key={index}>
                                                        {sch.date_of_week}: {sch.start_at} - {sch.end_at}  {/* ✅ Đảm bảo sử dụng đúng tên */}
                                                    </li>
                                                ))
                                            ) : (
                                                <span className="text-gray-500">Chưa có lịch học</span>
                                            )}
                                        </ul>
                                    </td>

                                    <td className="border p-2 text-center">
                                        <button
                                            onClick={() => handleRegister(cls.id)}
                                            className="px-3 py-1 bg-blue-500 text-white rounded hover:bg-blue-600"
                                        >
                                            Đăng ký
                                        </button>
                                    </td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan="6" className="border p-4 text-center text-gray-500">
                                    Không có lớp học nào.
                                </td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
            <button
                onClick={() => navigate("/")}
                className="mt-4 px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600"
            >
                Quay lại
            </button>
        </div>
    );
};

export default RegisterGroup;
