import { useState, useEffect } from 'react';
import './Salary.css';
// Giả sử bạn đã có API hoặc dữ liệu sẵn có cho lương giáo viên
const Salary = () => {
    const [teacherInfo, setTeacherInfo] = useState(null);
    const [salaryDetails, setSalaryDetails] = useState([]);
    const [totalSalary, setTotalSalary] = useState(0);

    // Giả lập dữ liệu giáo viên (có thể lấy từ API hoặc localStorage)
    useEffect(() => {
        // Thông tin giáo viên, bạn có thể lấy từ API hoặc từ localStorage
        const teacherData = JSON.parse(localStorage.getItem('user')); // Giả sử đã lưu thông tin giáo viên
        setTeacherInfo(teacherData);

        // Dữ liệu lương từ API (ví dụ, lấy thông tin về các lớp đã dạy, số giờ dạy, lương mỗi giờ)
        const fetchedSalaryDetails = [
            { className: 'Math 101', hours: 20, hourlyRate: 50 },
            { className: 'English 101', hours: 15, hourlyRate: 45 },
            { className: 'Physics 101', hours: 10, hourlyRate: 55 },
        ];
        setSalaryDetails(fetchedSalaryDetails);

        // Tính tổng lương
        const total = fetchedSalaryDetails.reduce(
            (sum, detail) => sum + detail.hours * detail.hourlyRate,
            0
        );
        setTotalSalary(total);
    }, []);

    return (
        <div className="salary-container">
            {teacherInfo ? (
                <>
                    <h2>Thông tin Lương</h2>
                    <div className="teacher-info">
                        <h3>Giáo viên: {teacherInfo.name}</h3>
                        <p>ID Giáo viên: {teacherInfo.id}</p>
                    </div>

                    <h3>Danh sách các lớp đã dạy:</h3>
                    <table>
                        <thead>
                            <tr>
                                <th>Tên Lớp</th>
                                <th>Số Giờ Dạy</th>
                                <th>Lương mỗi Giờ</th>
                                <th>Tổng Lương</th>
                            </tr>
                        </thead>
                        <tbody>
                            {salaryDetails.map((detail, index) => (
                                <tr key={index}>
                                    <td>{detail.className}</td>
                                    <td>{detail.hours}</td>
                                    <td>{detail.hourlyRate} VND</td>
                                    <td>{detail.hours * detail.hourlyRate} VND</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>

                    <div className="total-salary">
                        <h4>Tổng Lương: {totalSalary} VND</h4>
                    </div>

                    <div className="update-salary">
                        <button onClick={() => alert('Cập nhật lương')}>Cập nhật lương</button>
                    </div>
                </>
            ) : (
                <p>Đang tải thông tin giáo viên...</p>
            )}
        </div>
    );
};

export default Salary;
