import React, { useState, useEffect } from "react";
import axios from "axios";

const Notifications = ({ userRole, token }) => {
    const [notifications, setNotifications] = useState([]);
    const [newNotification, setNewNotification] = useState({ title: "", message: "", recipient: "all" });

    useEffect(() => {
        fetchNotifications();
    }, []);

    const fetchNotifications = async () => {
        try {
            const res = await axios.get("http://localhost:5000/api/notifications", {
                headers: { Authorization: `Bearer ${token}` },
            });
            setNotifications(res.data);
        } catch (error) {
            console.error("L·ªói l·∫•y th√¥ng b√°o:", error);
        }
    };

    const handleCreateNotification = async () => {
        if (!newNotification.title || !newNotification.message) {
            alert("Ti√™u ƒë·ªÅ v√† n·ªôi dung kh√¥ng ƒë∆∞·ª£c ƒë·ªÉ tr·ªëng!");
            return;
        }
        try {
            await axios.post("http://localhost:5000/api/notifications", newNotification, {
                headers: { Authorization: `Bearer ${token}` },
            });
            fetchNotifications(); // Refresh danh s√°ch sau khi t·∫°o
            setNewNotification({ title: "", message: "", recipient: "all" });
        } catch (error) {
            console.error("L·ªói t·∫°o th√¥ng b√°o:", error);
        }
    };

    const handleDeleteNotification = async (id) => {
        if (!window.confirm("B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a th√¥ng b√°o n√†y?")) return;
        try {
            await axios.delete(`http://localhost:5000/api/notifications/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            fetchNotifications(); // Refresh danh s√°ch sau khi x√≥a
        } catch (error) {
            console.error("L·ªói x√≥a th√¥ng b√°o:", error);
        }
    };

    return (
        <div className="p-4">
            <h2 className="text-xl font-bold mb-4">üì¢ Danh s√°ch th√¥ng b√°o</h2>
            {userRole === "ad" || userRole === "cm" ? (
                <div className="mb-4 p-4 border rounded">
                    <h3 className="font-semibold">üìù T·∫°o th√¥ng b√°o m·ªõi</h3>
                    <input
                        type="text"
                        placeholder="Ti√™u ƒë·ªÅ"
                        value={newNotification.title}
                        onChange={(e) => setNewNotification({ ...newNotification, title: e.target.value })}
                        className="block w-full p-2 border mt-2"
                    />
                    <textarea
                        placeholder="N·ªôi dung"
                        value={newNotification.message}
                        onChange={(e) => setNewNotification({ ...newNotification, message: e.target.value })}
                        className="block w-full p-2 border mt-2"
                    />
                    <select
                        value={newNotification.recipient}
                        onChange={(e) => setNewNotification({ ...newNotification, recipient: e.target.value })}
                        className="block w-full p-2 border mt-2"
                    >
                        <option value="all">T·∫•t c·∫£</option>
                        <option value="hv">H·ªçc vi√™n</option>
                        <option value="gv">Gi·∫£ng vi√™n</option>
                        <option value="cm">Qu·∫£n l√Ω trung t√¢m</option>
                        <option value="ad">Admin</option>
                    </select>
                    <button onClick={handleCreateNotification} className="mt-2 bg-blue-500 text-white p-2 rounded">
                        ‚ûï Th√™m th√¥ng b√°o
                    </button>
                </div>
            ) : null}

            {notifications.length === 0 ? (
                <p>Kh√¥ng c√≥ th√¥ng b√°o n√†o.</p>
            ) : (
                <ul className="list-none">
                    {notifications.map((notification) => (
                        <li key={notification.id} className="p-3 border-b flex justify-between">
                            <div>
                                <h3 className="font-bold">{notification.title}</h3>
                                <p>{notification.message}</p>
                                <small className="text-gray-500">üïí {new Date(notification.created_at).toLocaleString()}</small>
                            </div>
                            {userRole === "ad" && (
                                <button
                                    onClick={() => handleDeleteNotification(notification.id)}
                                    className="bg-red-500 text-white p-2 rounded"
                                >
                                    ‚ùå X√≥a
                                </button>
                            )}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default Notifications;
