import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const RegisterGroup = () => {
    const navigate = useNavigate();
    const [groups, setGroups] = useState([]);
    const currentUser = JSON.parse(localStorage.getItem("user"));

    useEffect(() => {
        if (!currentUser) {
            navigate("/login");
        } else {
            fetchGroups();
        }
    }, [navigate]);

    const fetchGroups = async () => {
        try {
            const res = await axios.get("http://localhost:5000/api/groups");
            setGroups(res.data);
        } catch (err) {
            console.error("Lỗi tải nhóm học:", err);
        }
    };

    const handleRegister = async (groupId) => {
        try {
            const response = await axios.post(
                "http://localhost:5000/api/register-group",
                { userId: currentUser.id, groupId },
                { headers: { Authorization: `Bearer ${currentUser.token}` } }
            );
            alert(response.data.message);
            fetchGroups();
        } catch (error) {
            console.error("Lỗi đăng ký nhóm:", error);
            alert("Đăng ký thất bại!");
        }
    };

    return (
        <div className="register-group-container">
            <h2>Đăng ký nhóm học</h2>
            <table className="group-table">
                <thead>
                    <tr>
                        <th>Tên nhóm</th>
                        <th>Giáo viên</th>
                        <th>Lịch học</th>
                        <th>Đăng ký</th>
                    </tr>
                </thead>
                <tbody>
                    {groups.map((group) => (
                        <tr key={group.id}>
                            <td>{group.name}</td>
                            <td>{group.teacher}</td>
                            <td>{group.schedule}</td>
                            <td>
                                <button onClick={() => handleRegister(group.id)}>Đăng ký</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button onClick={() => navigate("/")}>Quay lại</button>
        </div>
    );
};

export default RegisterGroup;
