import { useState, useEffect, useRef } from "react";
import StudentClasses from "./student";
import TeacherClasses from "./teacher";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";
import { FaTrash } from "react-icons/fa";
import "react-toastify/dist/ReactToastify.css";
import "./Documents.css";

const Courses = ({ user }) => {
    const [selectedClass, setSelectedClass] = useState(null);
    const [assignments, setAssignments] = useState([]);
    const navigate = useNavigate();
    const [selectedFile, setSelectedFile] = useState(null);
    const fileInputRef = useRef(null);
    const storedUser = localStorage.getItem("user");
    const [currentUser, setCurrentUser] = useState(user || (storedUser ? JSON.parse(storedUser) : null));
    const [documents, setDocuments] = useState([]);

    useEffect(() => {
        if (!currentUser) {
            navigate("/login");
        }
    }, [currentUser, navigate]);

    const handleUploadClick = () => {
        if (fileInputRef.current) {
            fileInputRef.current.click();
        }
    };

    const handleSelectClass = async (class_Id) => {
        try {
            const response = await axios.get(`http://localhost:5000/documents/class/${class_Id}`);
            setDocuments(response.data);
            setSelectedClass(class_Id);
        } catch (error) {
            console.error("‚ùå L·ªói khi l·∫•y t√†i li·ªáu:", error);
            toast.error("‚ö†Ô∏è Kh√¥ng th·ªÉ t·∫£i t√†i li·ªáu!");
        }
    };

    const handleFileChange = async (event) => {
        const file = event.target.files[0];
        if (!file) return;

        if (!currentUser || !currentUser.id) {
            toast.error("‚ö†Ô∏è Kh√¥ng th·ªÉ x√°c ƒë·ªãnh ng∆∞·ªùi d√πng! H√£y ƒëƒÉng nh·∫≠p l·∫°i.");
            return;
        }

        const formData = new FormData();
        formData.append("file", file);
        formData.append("class_id", selectedClass);
        formData.append("student_id", currentUser.id);

        try {
            await axios.post("http://localhost:5000/upload-assignment", formData, {
                headers: { "Content-Type": "multipart/form-data" },
            });

            toast.success("‚úÖ N·ªôp b√†i t·∫≠p th√†nh c√¥ng!");
            handleSelectClass(selectedClass);
        } catch (error) {
            console.error("‚ùå L·ªói khi n·ªôp b√†i t·∫≠p:", error);
            toast.error("‚ö†Ô∏è Kh√¥ng th·ªÉ n·ªôp b√†i t·∫≠p!");
        }
    };

    return (
        <div className="courses-container">
            <h2>B√†i t·∫≠p l·ªõp h·ªçc</h2>

            {!selectedClass ? (
                <>
                    <h3>Ch·ªçn l·ªõp ƒë·ªÉ xem b√†i t·∫≠p</h3>
                    {currentUser?.role === "hv" ? (
                        <StudentClasses userId={currentUser.id} onSelectClass={handleSelectClass} />
                    ) : currentUser?.role === "gv" ? (
                        <TeacherClasses userId={currentUser.id} onSelectClass={handleSelectClass} />
                    ) : (
                        <p>ƒêang t·∫£i d·ªØ li·ªáu ng∆∞·ªùi d√πng...</p>
                    )}
                </>
            ) : (
                <div className="assignment-list">
                    <button onClick={() => setSelectedClass(null)}>üîô Quay l·∫°i danh s√°ch l·ªõp</button>
                    <h3>Danh s√°ch b√†i t·∫≠p</h3>
                    {currentUser?.role === "hv" && (
                        <div className="upload-section">
                            <input
                                type="file"
                                accept=".pdf,.docx,.pptx"
                                ref={fileInputRef}
                                style={{ display: "none" }}
                                onChange={handleFileChange}
                            />
                            <button onClick={handleUploadClick}>üì§ N·ªôp b√†i t·∫≠p</button>
                        </div>
                    )}

                    <ul>
                        {assignments.length > 0 ? (
                            assignments.map((assignment) => (
                                <li key={assignment.id}>
                                    <a
                                        href={`http://localhost:5000${assignment.file_path}`}
                                        target="_blank"
                                        rel="noopener noreferrer"
                                    >
                                        {assignment.title}
                                    </a>
                                </li>
                            ))
                        ) : (
                            <p>Kh√¥ng c√≥ b√†i t·∫≠p n√†o.</p>
                        )}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default Courses;
