import { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import { FaUser, FaLock } from "react-icons/fa"; // Import icon
import "./Login.css";

const Login = ({ setUser }) => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [errorMessage, setErrorMessage] = useState("");
    const navigate = useNavigate();
    useEffect(() => {
        document.body.classList.add("login-page");
        return () => {
            document.body.classList.remove("login-page"); // X√≥a khi r·ªùi trang
        };
    }, []);
    const handleLogin = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch("http://localhost:5000/login", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ username, password }),
            });

            const data = await response.json();

            console.log("D·ªØ li·ªáu t·ª´ API:", data);

            if (response.ok) {
                const user = data.user;
                localStorage.setItem(`user`, JSON.stringify(user)); // üëà L∆∞u theo role ri√™ng bi·ªát
                setUser(user);
                navigate("/home");

            } else {
                setErrorMessage(data.error || "ƒêƒÉng nh·∫≠p th·∫•t b·∫°i");
            }
        } catch (error) {
            console.error("L·ªói k·∫øt n·ªëi:", error);
            setErrorMessage("L·ªói k·∫øt n·ªëi ƒë·∫øn server!");
        }
    };

    return (
        <div className="auth-container">
            <h2>ƒêƒÉng Nh·∫≠p</h2>
            <form onSubmit={handleLogin}>
                <div className="input-group">
                    <input
                        type="text"
                        placeholder="Nh·∫≠p t√™n ƒëƒÉng nh·∫≠p"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                    />
                    <FaUser className="icon" />
                </div>
                <div className="input-group">
                    <input
                        type="password"
                        placeholder="Nh·∫≠p m·∫≠t kh·∫©u"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    <FaLock className="icon" />
                </div>
                <button type="submit">ƒêƒÉng Nh·∫≠p</button>
            </form>
            {errorMessage && <p className="error">{errorMessage}</p>}
            <p>Ch∆∞a c√≥ t√†i kho·∫£n? <Link to="/register">ƒêƒÉng k√Ω ngay</Link></p>
        </div>
    );
};

export default Login;
