import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Register.css";

const Register = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        fullName: "",
        email: "",
        username: "",
        password: "",
        confirmPassword: "",
        role: "hv", // Mặc định là học viên
    });

    const [errors, setErrors] = useState({});

    // Xử lý thay đổi input
    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    // Kiểm tra dữ liệu trước khi gửi
    const validate = () => {
        let newErrors = {};
        if (!formData.fullName.trim()) newErrors.fullName = "Họ và tên không được để trống!";
        if (!formData.email.trim()) newErrors.email = "Email không được để trống!";

        if (!formData.username.trim()) newErrors.username = "Tên đăng nhập không được để trống!";
        if (formData.username.length < 4) newErrors.username = "Tên đăng nhập phải có ít nhất 4 ký tự!";
        if (formData.password.length < 6) newErrors.password = "Mật khẩu phải có ít nhất 6 ký tự!";
        if (formData.password !== formData.confirmPassword) newErrors.confirmPassword = "Mật khẩu xác nhận không khớp!";
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0; // Nếu không có lỗi, trả về true
    };

    // Xử lý đăng ký
    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!validate()) return; // Nếu dữ liệu không hợp lệ, dừng lại

        try {
            const { confirmPassword, ...dataToSend } = formData; // Bỏ confirmPassword khi gửi đi
            const response = await axios.post("http://localhost:5000/register", dataToSend);
            alert(response.data.message);
            navigate("/login");
        } catch (error) {
            alert("Đăng ký thất bại, vui lòng thử lại!");
        }
    };

    return (
        <div className="register-container">
            <h2>📝 Đăng ký tài khoản</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" name="fullName" placeholder="Họ và tên" onChange={handleChange} />
                {errors.fullName && <p className="error">{errors.fullName}</p>}
                <input type="email" name="email" placeholder="Email" onChange={handleChange} />
                {errors.email && <p className="error">{errors.email}</p>}


                <input type="text" name="username" placeholder="Tên đăng nhập" onChange={handleChange} />
                {errors.username && <p className="error">{errors.username}</p>}

                <input type="password" name="password" placeholder="Mật khẩu" onChange={handleChange} />
                {errors.password && <p className="error">{errors.password}</p>}

                <input type="password" name="confirmPassword" placeholder="Xác nhận mật khẩu" onChange={handleChange} />
                {errors.confirmPassword && <p className="error">{errors.confirmPassword}</p>}

                <button type="submit">✅ Đăng ký</button>
            </form>
            <p>Đã có tài khoản? <span onClick={() => navigate("/login")}>Đăng nhập</span></p>
        </div>
    );
};

export default Register;
