import { useState, useEffect } from 'react';
import { useGroup } from './GroupContext';

// Dữ liệu mẫu cho học viên (dummy data)
const dummyStudentData = {
    id: 1,
    name: 'Nguyễn Văn A',
    role: 'hv',
    email: 'nguyenvana@example.com'
};

const GroupManagement = () => {
    const { groupData, updateGroupData } = useGroup();
    const [groupName, setGroupName] = useState(groupData ? groupData.name : '');
    const [startDate, setStartDate] = useState(groupData ? groupData.startDate : '');
    const [classTime, setClassTime] = useState(groupData ? groupData.classTime : '');

    useEffect(() => {
        if (groupData) {
            setGroupName(groupData.name);
            setStartDate(groupData.startDate);
            setClassTime(groupData.classTime);
        }
    }, [groupData]);

    const handleSubmit = () => {
        // Tạo dữ liệu nhóm mới từ các trường input
        const newGroupData = {
            name: groupName,
            startDate: startDate,
            classTime: classTime,
        };

        // Cập nhật dữ liệu nhóm trong context và lưu vào localStorage
        updateGroupData(newGroupData);
        localStorage.setItem('groupData', JSON.stringify(newGroupData));

        // Kiểm tra dữ liệu học viên trong localStorage
        let user = JSON.parse(localStorage.getItem('user'));
        // Nếu không có dữ liệu học viên, sử dụng dữ liệu mẫu
        if (!user) {
            user = dummyStudentData;
            localStorage.setItem('user', JSON.stringify(user));
        }

        // Nếu vai trò của user là học viên, cập nhật thông tin nhóm cho user
        if (user.role === 'hv') {
            const updatedUser = { ...user, group: groupName };
            localStorage.setItem('user', JSON.stringify(updatedUser));
            console.log('Updated user:', updatedUser); // Debug: kiểm tra dữ liệu học viên sau cập nhật
            alert('Nhóm đã được tạo và dữ liệu học viên đã được cập nhật (sử dụng dữ liệu mẫu).');
        } else {
            alert('Nhóm đã được tạo, nhưng dữ liệu học viên không được cập nhật.');
        }
    };

    return (
        <div>
            <h1>Quản lý nhóm</h1>

            {groupData ? (
                <div>
                    <h2>{groupData.name}</h2>
                    <p>Ngày bắt đầu: {groupData.startDate}</p>
                    <p>Thời gian học: {groupData.classTime}</p>
                </div>
            ) : (
                <p>Chưa có dữ liệu nhóm.</p>
            )}

            <div>
                <div>
                    <label>Tên nhóm:</label>
                    <input
                        type="text"
                        value={groupName}
                        onChange={(e) => setGroupName(e.target.value)}
                    />
                </div>
                <div>
                    <label>Ngày bắt đầu:</label>
                    <input
                        type="date"
                        value={startDate}
                        onChange={(e) => setStartDate(e.target.value)}
                    />
                </div>
                <div>
                    <label>Thời gian học:</label>
                    <input
                        type="text"
                        value={classTime}
                        onChange={(e) => setClassTime(e.target.value)}
                    />
                </div>
                <button onClick={handleSubmit}>Tạo nhóm mới</button>
            </div>
        </div>
    );
};

export default GroupManagement;
