import { useState, useEffect } from 'react';
import './Statistics.css';

function Statistics() {
    const [revenueData, setRevenueData] = useState([]);
    const [totalRevenue, setTotalRevenue] = useState(0);

    useEffect(() => {
        // Giả sử đây là dữ liệu doanh thu, bạn có thể thay thế bằng API thực tế
        const fetchRevenueData = () => {
            const data = [
                { month: 'January', revenue: 150000000 }, // 150 triệu VNĐ
                { month: 'February', revenue: 180000000 }, // 180 triệu VNĐ
                { month: 'March', revenue: 200000000 }, // 200 triệu VNĐ
                { month: 'April', revenue: 210000000 }, // 210 triệu VNĐ
                { month: 'May', revenue: 175000000 }, // 175 triệu VNĐ
                { month: 'June', revenue: 190000000 }, // 190 triệu VNĐ
            ];

            setRevenueData(data);

            // Tính tổng doanh thu
            const total = data.reduce((acc, curr) => acc + curr.revenue, 0);
            setTotalRevenue(total);
        };

        fetchRevenueData();
    }, []);

    return (
        <div className="statistics">
            <h1>Thống Kê Doanh Thu</h1>
            <div className="total-revenue">
                <h2>Tổng Doanh Thu: {totalRevenue.toLocaleString()} VNĐ</h2>
            </div>

            <div className="revenue-chart">
                <h3>Doanh Thu Theo Tháng</h3>
                <table>
                    <thead>
                        <tr>
                            <th>Tháng</th>
                            <th>Doanh Thu (VNĐ)</th>
                        </tr>
                    </thead>
                    <tbody>
                        {revenueData.map((item, index) => (
                            <tr key={index}>
                                <td>{item.month}</td>
                                <td>{item.revenue.toLocaleString()}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default Statistics;
