import React, { useEffect, useState } from "react";
import axios from "axios";

const Notifications = () => {
    const [notifications, setNotifications] = useState([]);
    const [newNotification, setNewNotification] = useState({ title: "", message: "", recipient: "users" });

    useEffect(() => {
        fetchNotifications();
    }, []);

    const fetchNotifications = async () => {
        try {
            const response = await axios.get("http://localhost:5000/notifications/notifications");
            setNotifications(response.data);
        } catch (error) {
            console.error("Error fetching notifications:", error);
        }
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/notifications/notifications/${id}`);
            fetchNotifications();
        } catch (error) {
            console.error("Error deleting notification:", error);
        }
    };

    const handleCreate = async () => {
        if (!newNotification.title || !newNotification.message) {
            alert("Vui lòng nhập tiêu đề và nội dung.");
            return;
        }
        try {
            await axios.post("http://localhost:5000/notifications/notifications", newNotification, {
                headers: { "Content-Type": "application/json" },
            });
            setNewNotification({ title: "", message: "", recipient: "users" });
            fetchNotifications();
        } catch (error) {
            console.error("Error creating notification:", error.response ? error.response.data : error.message);
            alert("Lỗi tạo thông báo: " + (error.response?.data?.message || error.message));
        }
    };

    return (
        <div>
            <h2>Notifications</h2>
            <div>
                <input
                    type="text"
                    placeholder="Title"
                    value={newNotification.title}
                    onChange={(e) => setNewNotification({ ...newNotification, title: e.target.value })}
                />
                <textarea
                    placeholder="Message"
                    value={newNotification.message}
                    onChange={(e) => setNewNotification({ ...newNotification, message: e.target.value })}
                />
                {/* Dropdown chọn vai trò */}
                <select
                    value={newNotification.recipient}
                    onChange={(e) => setNewNotification({ ...newNotification, recipient: e.target.value })}
                >
                    <option value="users">Tất cả người dùng</option>
                    <option value="hv">Học viên</option>
                    <option value="gv">Giáo viên</option>
                    <option value="cm">Quản lý</option>
                    <option value="ad">Admin</option>
                </select>
                <button onClick={handleCreate}>Add Notification</button>
            </div>
            <ul>
                {notifications.map((notification) => (
                    <li key={notification.id}>
                        <h4>{notification.title} ({notification.recipient})</h4>
                        <p>{notification.message}</p>
                        <button onClick={() => handleDelete(notification.id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Notifications;
