import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { GroupProvider } from './GroupContext';
import ProtectedRoute from './ProtectedRoute';

import Home from './home';
import Login from './Login';
import Register from './register';
import PersonalInfo from './PersonalInfo';
import Attendance from './Attendance';
import Grades from './Grades';
import PaymentHistory from './PaymentHistory';
import Schedule from './Schedule';
import Statistics from './Statistics';
import StudentManagement from './StudentManagement';
import GroupManagement from './GroupManagement';
import AttendanceSummary from './AttendanceSummary';
import AttendanceAlert from './AttendanceAlert';
import StudentGrouping from './StudentGrouping';
import TeacherSalary from './TeacherSalary';
import Salary from './Salary';
import History from './History';
import StudentSchedule from './StudentSchedule';
import RegisterClass from './RegisterClass';

function App() {
  const [user] = useState(() => {
    const storedUser = JSON.parse(localStorage.getItem('user'));
    return storedUser || null;
  });

  const isLoggedIn = !!user;
  const userRole = user?.role;

  useEffect(() => {
    console.log("🔹 User từ localStorage:", user);
  }, [user]);

  return (
    <Router>
      <GroupProvider>
        <Routes>
          {/* Trang chính */}
          <Route path="/" element={<Login />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route
            path="/home/*"
            element={
              isLoggedIn ? (
                <Home isLoggedIn={isLoggedIn} userRole={userRole} />
              ) : (
                <Navigate to="/login" />
              )
            }
          />

          {/* Các tuyến đường được bảo vệ */}
          {isLoggedIn && (
            <>
              {/* Admin & Center Manager */}
              <Route path="/home/group-management" element={<ProtectedRoute roles={['ad', 'cm']}><GroupManagement /></ProtectedRoute>} />
              <Route path="/home/schedule" element={<ProtectedRoute roles={['ad', 'cm', 'gv']}><Schedule /></ProtectedRoute>} />
              <Route path="/home/statistics" element={<ProtectedRoute roles={['cm']}><Statistics /></ProtectedRoute>} />
              <Route path="/home/student-management" element={<ProtectedRoute roles={['cm']}><StudentManagement /></ProtectedRoute>} />

              {/* Chấm công & Điểm danh */}
              <Route path="/home/attendance-summary" element={<ProtectedRoute roles={['ad']}><AttendanceSummary /></ProtectedRoute>} />
              <Route path="/home/attendance" element={<ProtectedRoute roles={['cm', 'gv']}><Attendance /></ProtectedRoute>} />
              <Route path="/home/grades" element={<ProtectedRoute roles={['cm', 'gv']}><Grades /></ProtectedRoute>} />
              <Route path="/home/attendance-report" element={<ProtectedRoute roles={['ad']}><AttendanceAlert /></ProtectedRoute>} />
              <Route path="/home/student-grouping" element={<ProtectedRoute roles={['ad']}><StudentGrouping /></ProtectedRoute>} />

              {/* Lương & Thanh toán */}
              <Route path="/home/teacher-payment" element={<ProtectedRoute roles={['ad']}><TeacherSalary /></ProtectedRoute>} />
              <Route path="/home/teacher-salary" element={<ProtectedRoute roles={['ad', 'cm', 'gv']}><TeacherSalary /></ProtectedRoute>} />
              <Route path="/home/student-payment-confirmation" element={<ProtectedRoute roles={['ad']}><PaymentHistory /></ProtectedRoute>} />
              <Route path="/home/salary" element={<ProtectedRoute roles={['gv']}><Salary /></ProtectedRoute>} />
              <Route path="/home/paymenthistory/hv" element={<ProtectedRoute roles={['hv']}><PaymentHistory /></ProtectedRoute>} />

              {/* Giảng viên */}
              <Route path="/home/history" element={<ProtectedRoute roles={['gv']}><History /></ProtectedRoute>} />

              {/* Học viên */}
              <Route path="/home/student-schedule" element={<ProtectedRoute roles={['hv']}><StudentSchedule /></ProtectedRoute>} />
              <Route path="/home/registerclass/hv" element={<ProtectedRoute roles={['hv']}><RegisterClass /></ProtectedRoute>} />

              {/* Thông tin cá nhân */}
              <Route path="/home/personal-info" element={<ProtectedRoute roles={['ad', 'cm', 'gv', 'hv']}><PersonalInfo /></ProtectedRoute>} />
            </>
          )}

          {/* Trang lỗi */}
          <Route path="*" element={<div>404 - Không tìm thấy trang</div>} />
        </Routes>
      </GroupProvider>
    </Router>
  );
}

export default App;
