import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Grades.css";

const Grades = ({ user }) => {
    const navigate = useNavigate();
    const [grades, setGrades] = useState([]);
    const [loading, setLoading] = useState(true);
    const [selectedClassId, setSelectedClassId] = useState(null);

    useEffect(() => {
        if (!user) {
            const storedUser = JSON.parse(localStorage.getItem("user"));
            if (!storedUser) {
                navigate("/login");
                return;
            }
            const storedClassId = localStorage.getItem("classId");
            if (storedClassId) fetchGrades(storedUser.id, storedClassId);
        } else {
            const storedClassId = localStorage.getItem("classId");
            if (storedClassId) fetchGrades(user.id, storedClassId);
        }
    }, [user, navigate, selectedClassId]); // ‚úÖ L·∫Øng nghe thay ƒë·ªïi classId

    const fetchGrades = async (userId, classId) => {
        if (!classId) {
            console.warn("‚ö†Ô∏è classId ch∆∞a ƒë∆∞·ª£c ch·ªçn, kh√¥ng g·ªçi API.");
            setGrades([]); // ‚úÖ Reset b·∫£ng khi ch∆∞a ch·ªçn l·ªõp
            return;
        }

        setSelectedClassId(classId); // ‚úÖ C·∫≠p nh·∫≠t classId m·ªõi
        setGrades([]); // ‚úÖ Reset tr∆∞·ªõc khi fetch d·ªØ li·ªáu m·ªõi
        setLoading(true);

        try {
            console.log(`üìå ƒêang fetch: http://localhost:5000/student/grades/${userId}/${classId}`);
            const response = await fetch(`http://localhost:5000/student/grades/${userId}/${classId}`);
            console.log("üìå Status:", response.status);
            const text = await response.text();
            console.log("üìå Response:", text);

            if (!response.ok) throw new Error(`L·ªói khi t·∫£i ƒëi·ªÉm s·ªë: ${response.status}`);

            const data = JSON.parse(text);
            setGrades(data);
        } catch (error) {
            console.error("‚ùå L·ªói:", error);
            setGrades([]); // ‚úÖ Tr√°nh l·ªói khi fetch th·∫•t b·∫°i
        } finally {
            setLoading(false);
        }
    };

    // ‚úÖ L·∫•y th√¥ng tin l·ªõp t·ª´ localStorage (n·∫øu API ch∆∞a tr·∫£ v·ªÅ `class_name` v√† `subject`)
    const classList = JSON.parse(localStorage.getItem("classList")) || [];
    const getClassInfo = (classId) => {
        const classData = classList.find(c => c.id === classId);
        return classData ? { name: classData.name, subject: classData.subject } : { name: "N/A", subject: "N/A" };
    };

    return (
        <div className="grades-container">
            <h2>üìä ƒêi·ªÉm s·ªë c·ªßa b·∫°n</h2>
            {loading ? (
                <p className="loading">ƒêang t·∫£i...</p>
            ) : grades.length === 0 && selectedClassId ? (
                <p>üìå Ch∆∞a c√≥ ƒëi·ªÉm cho l·ªõp {selectedClassId}.</p>
            ) : grades.length === 0 ? (
                <p>‚ùå Ch∆∞a ch·ªçn l·ªõp h·ªçc ƒë·ªÉ xem ƒëi·ªÉm.</p>
            ) : (
                <table className="grades-table">
                    <thead>
                        <tr>
                            <th>üè´ L·ªõp</th>
                            <th>üìñ M√¥n h·ªçc</th>
                            <th>üìù T√™n b√†i ki·ªÉm tra</th>
                            <th>üìä ƒêi·ªÉm s·ªë</th>
                            <th>üìÖ Ng√†y thi</th>
                        </tr>
                    </thead>
                    <tbody>
                        {grades.map((grade, index) => {
                            const classInfo = getClassInfo(grade.class_id); // ‚úÖ L·∫•y th√¥ng tin l·ªõp h·ªçc
                            return (
                                <tr key={index}>
                                    <td>{classInfo.name}</td>
                                    <td>{classInfo.subject}</td>
                                    <td>{grade.exam_name}</td>
                                    <td>{grade.score}</td>
                                    <td>{new Date(grade.exam_date).toLocaleDateString()}</td>
                                </tr>
                            );
                        })}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default Grades;
