import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Grades.css";

const Grades = ({ user }) => {
    const navigate = useNavigate();
    const [grades, setGrades] = useState([]);
    const [loading, setLoading] = useState(true);
    const [selectedClassId, setSelectedClassId] = useState(null);

    useEffect(() => {
        if (!user) {
            const storedUser = JSON.parse(localStorage.getItem("user"));
            if (!storedUser) {
                navigate("/login");
                return;
            }
            const storedClassId = localStorage.getItem("classId");
            if (storedClassId) fetchGrades(storedUser.id, storedClassId);
        } else {
            const storedClassId = localStorage.getItem("classId");
            if (storedClassId) fetchGrades(user.id, storedClassId);
        }
    }, [user, navigate, selectedClassId]); // ✅ Lắng nghe thay đổi classId

    const fetchGrades = async (userId, classId) => {
        if (!classId) {
            console.warn("⚠️ classId chưa được chọn, không gọi API.");
            setGrades([]); // ✅ Reset bảng khi chưa chọn lớp
            return;
        }

        setSelectedClassId(classId); // ✅ Cập nhật classId mới
        setGrades([]); // ✅ Reset trước khi fetch dữ liệu mới
        setLoading(true);

        try {
            console.log(`📌 Đang fetch: http://localhost:5000/student/grades/${userId}/${classId}`);
            const response = await fetch(`http://localhost:5000/student/grades/${userId}/${classId}`);
            console.log("📌 Status:", response.status);
            const text = await response.text();
            console.log("📌 Response:", text);

            if (!response.ok) throw new Error(`Lỗi khi tải điểm số: ${response.status}`);

            const data = JSON.parse(text);
            setGrades(data);
        } catch (error) {
            console.error("❌ Lỗi:", error);
            setGrades([]); // ✅ Tránh lỗi khi fetch thất bại
        } finally {
            setLoading(false);
        }
    };

    // ✅ Lấy thông tin lớp từ localStorage (nếu API chưa trả về `class_name` và `subject`)
    const classList = JSON.parse(localStorage.getItem("classList")) || [];
    const getClassInfo = (classId) => {
        const classData = classList.find(c => c.id === classId);
        return classData ? { name: classData.name, subject: classData.subject } : { name: "N/A", subject: "N/A" };
    };

    return (
        <div className="grades-container">
            <h2>📊 Điểm số của bạn</h2>
            {loading ? (
                <p className="loading">Đang tải...</p>
            ) : grades.length === 0 && selectedClassId ? (
                <p>📌 Chưa có điểm cho lớp {selectedClassId}.</p>
            ) : grades.length === 0 ? (
                <p>❌ Chưa chọn lớp học để xem điểm.</p>
            ) : (
                <table className="grades-table">
                    <thead>
                        <tr>
                            <th>🏫 Lớp</th>
                            <th>📖 Môn học</th>
                            <th>📝 Tên bài kiểm tra</th>
                            <th>📊 Điểm số</th>
                            <th>📅 Ngày thi</th>
                        </tr>
                    </thead>
                    <tbody>
                        {grades.map((grade, index) => {
                            const classInfo = getClassInfo(grade.class_id); // ✅ Lấy thông tin lớp học
                            return (
                                <tr key={index}>
                                    <td>{classInfo.name}</td>
                                    <td>{classInfo.subject}</td>
                                    <td>{grade.exam_name}</td>
                                    <td>{grade.score}</td>
                                    <td>{new Date(grade.exam_date).toLocaleDateString()}</td>
                                </tr>
                            );
                        })}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default Grades;
