import React, { useState, useEffect } from "react";
import axios from "axios";

const TeacherGradeInput = () => {
    const [students, setStudents] = useState([]);
    const [grades, setGrades] = useState({});

    // Lấy danh sách học viên từ API
    useEffect(() => {
        axios.get("http://localhost:5000/student/user")
            .then(res => setStudents(res.data))
            .catch(err => console.error("Lỗi khi lấy danh sách học viên:", err));
    }, []);

    // Gửi điểm lên server
    const handleSubmit = (studentId) => {
        const { examName, score, examDate } = grades[studentId] || {};
        if (!examName || !score || !examDate) {
            alert("Vui lòng nhập đầy đủ thông tin");
            return;
        }

        axios.post("http://localhost:5000/student/grades", {
            student_id: studentId,
            exam_name: examName,
            score: parseFloat(score),
            exam_date: examDate
        }).then(() => {
            alert("Nhập điểm thành công!");
            setGrades(prev => ({ ...prev, [studentId]: { examName: "", score: "", examDate: "" } }));
        }).catch(err => console.error("Lỗi khi nhập điểm:", err));
    };

    // Cập nhật giá trị điểm cho học viên
    const handleChange = (studentId, field, value) => {
        setGrades(prev => ({
            ...prev,
            [studentId]: { ...prev[studentId], [field]: value }
        }));
    };

    return (
        <div>
            <h2>Nhập Điểm Cho Học Viên</h2>
            <table border="1">
                <thead>
                    <tr>
                        <th>Tên Học Viên</th>
                        <th>Tên Bài Kiểm Tra</th>
                        <th>Điểm</th>
                        <th>Ngày Thi</th>
                        <th>Hành Động</th>
                    </tr>
                </thead>
                <tbody>
                    {students.map(student => (
                        <tr key={student.id}>
                            <td>{student.fullName}</td>
                            <td><input type="text" placeholder="Tên bài kiểm tra" value={grades[student.id]?.examName || ""} onChange={(e) => handleChange(student.id, "examName", e.target.value)} /></td>
                            <td><input type="number" placeholder="Điểm" value={grades[student.id]?.score || ""} onChange={(e) => handleChange(student.id, "score", e.target.value)} /></td>
                            <td><input type="date" value={grades[student.id]?.examDate || ""} onChange={(e) => handleChange(student.id, "examDate", e.target.value)} /></td>
                            <td><button onClick={() => handleSubmit(student.id)}>Xác nhận</button></td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default TeacherGradeInput;
