import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { FaHome, FaBell, FaCog, FaUser, FaSignOutAlt, FaChartBar, FaCalendarAlt, FaDatabase, FaEnvelope } from "react-icons/fa";
import "./Home.css";
import RevenueChart from "./Revenue";

const Home = ({ user }) => {
    const navigate = useNavigate();
    const [currentUser, setCurrentUser] = useState(user || JSON.parse(localStorage.getItem("user")));
    const [showSettings, setShowSettings] = useState(false);
    const [notifications, setNotifications] = useState([]); // State l∆∞u th√¥ng b√°o

    useEffect(() => {
        if (!currentUser) {
            navigate("/login");
        } else {
            fetchNotifications(); // L·∫•y danh s√°ch th√¥ng b√°o khi load trang
        }
    }, [currentUser, navigate]);

    // G·ªçi API l·∫•y th√¥ng b√°o
    const fetchNotifications = async () => {
        try {
            const response = await fetch("http://localhost:5000/admins/notifications");
            const data = await response.json();
            setNotifications(data);
        } catch (error) {
            console.error("L·ªói khi l·∫•y th√¥ng b√°o:", error);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem("user");
        setCurrentUser(null);
        navigate("/login");
    };

    if (!currentUser) return <p>ƒêang t·∫£i...</p>;

    return (
        <div className="home-container">
            <nav className="navbar">
                <h1>Trung T√¢m Gia S∆∞</h1>
                <div className="navbar-buttons">
                    <span className="nav-item" onClick={() => navigate("/home")}><FaHome /> Trang ch·ªß</span>

                    {/* N√∫t th√¥ng b√°o */}
                    <span className="nav-item" onClick={fetchNotifications}>
                        <FaBell /> Th√¥ng b√°o {notifications.length > 0 && `(${notifications.length})`}
                    </span>

                    <div className="settings-container">
                        <span className="nav-item" onClick={() => setShowSettings(!showSettings)}>
                            <FaCog /> C√†i ƒë·∫∑t
                        </span>
                        {showSettings && (
                            <div className="settings-menu">
                                <span onClick={() => navigate("/settings/profile")}>üîπ Th√¥ng tin c√° nh√¢n</span>
                                <span onClick={() => navigate("/settings/password")}>üîπ ƒê·ªïi m·∫≠t kh·∫©u</span>
                                <span onClick={() => navigate("/settings/preferences")}>üîπ T√πy ch·ªânh giao di·ªán</span>
                            </div>
                        )}
                    </div>

                    <span className="nav-item" onClick={() => navigate("/profile")}><FaUser /> Th√¥ng tin c√° nh√¢n</span>
                    <span className="nav-item logout" onClick={handleLogout}><FaSignOutAlt /> ƒêƒÉng xu·∫•t</span>
                </div>
            </nav>

            <div className="main-content">
                <aside className="sidebar">
                    <ul>
                        <li className="sidebar-item" onClick={() => navigate("/home")}><FaHome /> Trang ch·ªß</li>
                    </ul>
                </aside>

                <main className="dashboard">
                    <section className="dashboard-content">
                        <h2>Th·ªëng k√™ doanh thu</h2>
                        <div className="chart-container">
                            <RevenueChart />
                        </div>

                        {/* Danh s√°ch th√¥ng b√°o */}
                        <h2>Th√¥ng b√°o</h2>
                        <div className="notifications-container">
                            {notifications.length === 0 ? (
                                <p>Kh√¥ng c√≥ th√¥ng b√°o n√†o.</p>
                            ) : (
                                notifications.map((noti, index) => (
                                    <div key={index} className="notification-item">
                                        <h4>{noti.title}</h4>
                                        <p>{noti.message}</p>
                                    </div>
                                ))
                            )}
                        </div>
                    </section>
                </main>
            </div>
        </div>
    );
};

export default Home;
