import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Register.css";

const Register = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        fullName: "",
        username: "",
        email: "",
        password: "",
        confirmPassword: "", // üÜï Th√™m tr∆∞·ªùng x√°c nh·∫≠n m·∫≠t kh·∫©u
        phone: "",
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Ki·ªÉm tra m·∫≠t kh·∫©u v√† x√°c nh·∫≠n m·∫≠t kh·∫©u
        if (formData.password !== formData.confirmPassword) {
            alert("M·∫≠t kh·∫©u kh√¥ng kh·ªõp, vui l√≤ng nh·∫≠p l·∫°i!");
            return;
        }

        try {
            const response = await fetch("http://localhost:5000/register", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    fullName: formData.fullName,
                    username: formData.username,
                    email: formData.email,
                    password: formData.password,
                    phone: formData.phone,
                }),
            });

            const data = await response.json();
            if (response.ok) {
                alert(data.message);
                navigate("/login");
            } else {
                alert(data.error);
            }
        } catch (error) {
            console.error("L·ªói:", error);
            alert("ƒêƒÉng k√Ω th·∫•t b·∫°i, vui l√≤ng th·ª≠ l·∫°i!");
        }
    };

    return (
        <div className="register-container">
            <h2>üìù ƒêƒÉng k√Ω</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" name="fullName" placeholder="H·ªç v√† t√™n" value={formData.fullName} onChange={handleChange} required />
                <input type="text" name="username" placeholder="T√™n ƒëƒÉng nh·∫≠p" value={formData.username} onChange={handleChange} required />
                <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
                <input type="text" name="phone" placeholder="S·ªë ƒëi·ªán tho·∫°i" value={formData.phone} onChange={handleChange} required />
                <input type="password" name="password" placeholder="M·∫≠t kh·∫©u" value={formData.password} onChange={handleChange} required />
                <input type="password" name="confirmPassword" placeholder="X√°c nh·∫≠n m·∫≠t kh·∫©u" value={formData.confirmPassword} onChange={handleChange} required />
                <button type="submit">ƒêƒÉng k√Ω</button>
            </form>
        </div>
    );
};

export default Register;
