import { useEffect, useState } from "react";
import axios from "axios";

const FinanceReports = () => {
    const [report, setReport] = useState({ totalFees: 0, totalSalary: 0, profit: 0 });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchReport = async () => {
            try {
                const response = await axios.get("http://localhost:5000/manager/finance");
                setReport(response.data);
            } catch (err) {
                console.error("Lỗi lấy dữ liệu tài chính:", err);
                setError("Không thể tải dữ liệu tài chính.");
            } finally {
                setLoading(false);
            }
        };
        fetchReport();
    }, []);

    if (loading) return <p>Đang tải dữ liệu...</p>;
    if (error) return <p style={{ color: "red" }}>{error}</p>;

    return (
        <div>
            <h2>Báo cáo tài chính</h2>
            <p><strong>Tổng học phí thu:</strong> {report.totalFees.toLocaleString()} VND</p>
            <p><strong>Tổng lương giáo viên:</strong> {report.totalSalary.toLocaleString()} VND</p>
            <p><strong>Lợi nhuận:</strong> {report.profit.toLocaleString()} VND</p>
        </div>
    );
};

export default FinanceReports;
