import { useState, useEffect } from "react";

function ManageGroups() {
    const [groups, setGroups] = useState([]);
    const [name, setName] = useState("");
    const [subject, setSubject] = useState("");
    const [type, setType] = useState("Normal");
    const [grade, setGrade] = useState("");
    const [maxStudents, setMaxStudents] = useState(50);
    const [selectedPeriods, setSelectedPeriods] = useState([]);

    // Fetch danh sách nhóm học
    useEffect(() => {
        fetch("http://localhost:5000/manager/groups")
            .then((res) => res.json())
            .then((data) => setGroups(data))
            .catch((err) => console.error(err));
    }, []);

    // Tạo nhóm học mới
    const handleCreateGroup = async (e) => {
        e.preventDefault();
        const newGroup = { name, subject, type, grade, max_student: maxStudents };

        const res = await fetch("http://localhost:5000/manager/groups", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(newGroup),
        });

        if (res.ok) {
            alert("Tạo nhóm học thành công!");
            window.location.reload();
        } else {
            alert("Lỗi khi tạo nhóm học!");
        }
    };

    // Gán lịch học cho nhóm
    const handleAssignPeriods = async (groupId) => {
        const res = await fetch(`http://localhost:5000/manager/groups/${groupId}/periods`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ periods: selectedPeriods }),
        });

        if (res.ok) {
            alert("Gán lịch học thành công!");
        } else {
            alert("Lỗi khi gán lịch!");
        }
    };

    return (
        <div>
            <h2>Quản lý Nhóm Học</h2>

            {/* Danh sách nhóm học */}
            <h3>Danh sách nhóm học</h3>
            <ul>
                {groups.map((g) => (
                    <li key={g.id}>
                        {g.name} - {g.subject} - {g.type}
                        <button onClick={() => handleAssignPeriods(g.id)}>Chọn Lịch Học</button>
                    </li>
                ))}
            </ul>

            {/* Form tạo nhóm học */}
            <h3>Tạo nhóm học mới</h3>
            <form onSubmit={handleCreateGroup}>
                <input type="text" placeholder="Tên nhóm" value={name} onChange={(e) => setName(e.target.value)} required />
                <input type="text" placeholder="Môn học" value={subject} onChange={(e) => setSubject(e.target.value)} required />
                <select value={type} onChange={(e) => setType(e.target.value)}>
                    <option value="Normal">Normal</option>
                    <option value="Advanced">Advanced</option>
                </select>
                <input type="number" placeholder="Khối lớp" value={grade} onChange={(e) => setGrade(e.target.value)} required />
                <input type="number" placeholder="Số lượng tối đa" value={maxStudents} onChange={(e) => setMaxStudents(e.target.value)} />
                <button type="submit">Tạo Nhóm</button>
            </form>

            {/* Chọn lịch học */}
            <h3>Chọn lịch học</h3>
            <label>
                <input type="checkbox" value="7" onChange={(e) => setSelectedPeriods([...selectedPeriods, 7])} /> Thứ 2, tiết 1
            </label>
            <label>
                <input type="checkbox" value="19" onChange={(e) => setSelectedPeriods([...selectedPeriods, 19])} /> Thứ 4, tiết 3
            </label>
            <label>
                <input type="checkbox" value="31" onChange={(e) => setSelectedPeriods([...selectedPeriods, 31])} /> Thứ 6, tiết 5
            </label>
        </div>
    );
}

export default ManageGroups;
