import { useEffect, useState } from "react";

const TeacherClassRegister = () => {
    const [classes, setClasses] = useState([]);
    const [assignedClasses, setAssignedClasses] = useState([]);
    const teacherId = JSON.parse(localStorage.getItem("user"))?.id;

    useEffect(() => {
        fetch("http://localhost:5000/teacher/classes/unassigned")
            .then((res) => res.json())
            .then((data) => {
                setClasses(data);
                console.log("üì¶ L·ªõp ch∆∞a c√≥ gi√°o vi√™n:", data);
            });

        fetch(`http://localhost:5000/teacher/${teacherId}/assigned-classes`)
            .then((res) => res.json())
            .then((data) => {
                setAssignedClasses(data);
                console.log("üìò L·ªõp ƒë√£ ƒëƒÉng k√Ω:", data);
            });
    }, [teacherId]);
    const splitSchedule = (schedule) => {
        if (!schedule) return [];
        return schedule.split(",").map((s) => s.trim().toLowerCase());
    }; const parseSchedule = (scheduleStr) => {
        if (!scheduleStr) return [];
        return scheduleStr.split(",").map((entry) => {
            const match = entry.trim().toLowerCase().match(/^(\w+)\s*\(([^-]+)-([^)]+)\)$/);
            if (match) {
                const day = match[1].trim();
                const startTime = match[2].trim();
                const endTime = match[3].trim();
                return { day, startTime, endTime };
            }
            return null;
        }).filter(Boolean);
    };


    const normalizeSchedule = (str) => str?.trim().toLowerCase().replace(/\s+/g, ""); // b·ªè kho·∫£ng tr·∫Øng, ch·ªØ th∆∞·ªùng h√≥a
    const isScheduleConflict = (newSchedule, currentAssigned) => {
        const newSlots = parseSchedule(newSchedule);

        for (const cls of currentAssigned) {
            const assignedSlots = parseSchedule(cls.schedule);
            for (const newSlot of newSlots) {
                for (const assignedSlot of assignedSlots) {
                    if (newSlot.day === assignedSlot.day) {
                        if (isTimeOverlap(newSlot.startTime, newSlot.endTime, assignedSlot.startTime, assignedSlot.endTime)) {
                            console.warn(`‚ùå Tr√πng l·ªãch: ${newSlot.day} ${newSlot.startTime}-${newSlot.endTime} v·ªõi l·ªõp ${cls.name}`);
                            return true;
                        }
                    }
                }
            }
        }

        return false;
    };


    const toMinutes = (timeStr) => {
        const [hours, minutes] = timeStr.split(":").map(Number);
        return hours * 60 + minutes;
    };

    const isTimeOverlap = (start1, end1, start2, end2) => {
        const s1 = toMinutes(start1);
        const e1 = toMinutes(end1);
        const s2 = toMinutes(start2);
        const e2 = toMinutes(end2);
        return s1 < e2 && s2 < e1;
    };


    const handleRegisterClass = async (classId, className, schedule) => {
        const currentAssigned = await fetchAssignedClasses(teacherId); // ƒë·∫£m b·∫£o l·∫•y b·∫£n m·ªõi nh·∫•t
        setAssignedClasses(currentAssigned); // c·∫≠p nh·∫≠t state

        const hasConflict = isScheduleConflict(schedule, currentAssigned); // truy·ªÅn v√†o danh s√°ch m·ªõi nh·∫•t
        if (hasConflict) {
            alert(`‚ö†Ô∏è Kh√¥ng th·ªÉ ƒëƒÉng k√Ω l·ªõp ${className} v√¨ tr√πng l·ªãch v·ªõi l·ªõp ƒë√£ d·∫°y.`);
            return;
        }
        fetch(`http://localhost:5000/teacher/classes/${classId}/assign-teacher`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ teacher_id: teacherId }),
        })
            .then((res) => res.json())
            .then((data) => {
                alert(`‚úÖ B·∫°n ƒë√£ ƒëƒÉng k√Ω d·∫°y l·ªõp ${className} th√†nh c√¥ng!`);
                // Xo√° l·ªõp kh·ªèi danh s√°ch l·ªõp ch∆∞a c√≥ gi√°o vi√™n
                setClasses(classes.filter((cls) => cls.id !== classId));
                // Refetch danh s√°ch l·ªõp ƒë√£ ƒëƒÉng k√Ω t·ª´ server ƒë·ªÉ ƒë·∫£m b·∫£o lu√¥n ƒë√∫ng
                fetch(`http://localhost:5000/teacher/${teacherId}/assigned-classes`)
                    .then((res) => res.json())
                    .then((newData) => setAssignedClasses(newData))
                    .catch((error) => console.error("‚ùå L·ªói khi c·∫≠p nh·∫≠t l·ªõp ƒë√£ ƒëƒÉng k√Ω:", error));
            })
            .catch((error) => console.error("‚ùå L·ªói khi ƒëƒÉng k√Ω l·ªõp:", error));
    };

    return (
        <div>
            <h2>Danh s√°ch l·ªõp ch∆∞a c√≥ gi√°o vi√™n</h2>
            <table border="1" cellPadding="8" cellSpacing="0">
                <thead>
                    <tr>
                        <th>T√™n l·ªõp</th>
                        <th>Tr√¨nh ƒë·ªô</th>
                        <th>Kh·ªëi</th>
                        <th>Sƒ© s·ªë</th>
                        <th>L·ªãch h·ªçc</th>
                        <th>H√†nh ƒë·ªông</th>
                    </tr>
                </thead>
                <tbody>
                    {classes.length > 0 ? (
                        classes.map((cls) => (
                            <tr key={cls.id}>
                                <td>{cls.name}</td>
                                <td>{cls.type_mapped}</td>
                                <td>{cls.grade}</td>
                                <td>{cls.current_student}/{cls.max_student}</td>
                                <td>{cls.schedule || "Ch∆∞a c√≥ l·ªãch"}</td>
                                <td>
                                    <button onClick={() => handleRegisterClass(cls.id, cls.name, cls.schedule)}>
                                        ƒêƒÉng k√Ω d·∫°y
                                    </button>
                                </td>
                            </tr>
