import { useEffect, useState } from "react";

const ManageTeachers = () => {
    const [teachers, setTeachers] = useState([]);
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    useEffect(() => {
        fetchTeachers();
    }, []);

    const fetchTeachers = () => {
        fetch("http://localhost:5000/manager/teachers")
            .then((response) => response.json())
            .then((data) => setTeachers(data))
            .catch((error) => console.error("Lỗi khi lấy giáo viên:", error));
    };

    const handleAddTeacher = (e) => {
        e.preventDefault();
        fetch("http://localhost:5000/manager/teachers", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ username, email, password }),
        })
            .then((response) => response.json())
            .then((data) => {
                alert(data.message);
                fetchTeachers(); // Cập nhật danh sách sau khi thêm
                setUsername("");
                setEmail("");
                setPassword("");
            })
            .catch((error) => console.error("Lỗi khi thêm giáo viên:", error));
    };

    return (
        <div>
            <h2>Quản lý giáo viên</h2>

            <form onSubmit={handleAddTeacher}>
                <input
                    type="text"
                    placeholder="Tên đăng nhập"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                />
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Mật khẩu"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit">Thêm giáo viên</button>
            </form>

            <table border="1">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Tên đăng nhập</th>
                        <th>Email</th>
                        <th>Ngày tạo</th>
                    </tr>
                </thead>
                <tbody>
                    {teachers.map((teacher) => (
                        <tr key={teacher.id}>
                            <td>{teacher.id}</td>
                            <td>{teacher.username}</td>
                            <td>{teacher.email}</td>
                            <td>{new Date(teacher.created_at).toLocaleDateString()}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ManageTeachers;
