import { useState } from "react";
import DraggablePopup from "./DraggablePopup";
import "./ChangePasswordModal.css"; // Import CSS ri√™ng

const ChangePasswordModal = ({ closeModal }) => {
    const [oldPassword, setOldPassword] = useState("");
    const [newPassword, setNewPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");

    const handleChangePassword = async () => {
        if (newPassword !== confirmPassword) {
            alert("M·∫≠t kh·∫©u m·ªõi kh√¥ng kh·ªõp!");
            return;
        }

        const storedUser = JSON.parse(localStorage.getItem("user"));
        if (!storedUser) {
            alert("L·ªói: Kh√¥ng t√¨m th·∫•y th√¥ng tin ng∆∞·ªùi d√πng!");
            return;
        }

        try {
            const response = await fetch("http://localhost:5000/change-password", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    userId: storedUser.id,
                    oldPassword,
                    newPassword,
                }),
            });

            const data = await response.json();
            if (response.ok) {
                alert("ƒê·ªïi m·∫≠t kh·∫©u th√†nh c√¥ng!");
                closeModal();
            } else {
                alert(data.error || "C√≥ l·ªói x·∫£y ra!");
            }
        } catch (error) {
            console.error("L·ªói khi ƒë·ªïi m·∫≠t kh·∫©u:", error);
        }
    };

    return (
        <>
            <div className="modal-overlay" onClick={closeModal}></div>
            <DraggablePopup>
                <div className="modal">
                    <div className="popup-header">
                        <h3>üîë ƒê·ªïi m·∫≠t kh·∫©u</h3>
                        <button className="close-btn" onClick={closeModal}>√ó</button>
                    </div>
                    <div className="modal-body">
                        <label>M·∫≠t kh·∫©u c≈©</label>
                        <input type="password" value={oldPassword} onChange={(e) => setOldPassword(e.target.value)} />

                        <label>M·∫≠t kh·∫©u m·ªõi</label>
                        <input type="password" value={newPassword} onChange={(e) => setNewPassword(e.target.value)} />

                        <label>X√°c nh·∫≠n m·∫≠t kh·∫©u</label>
                        <input type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} />
                    </div>
                    <div className="modal-footer">
                        <button className="confirm-btn" onClick={handleChangePassword}>X√°c nh·∫≠n</button>
                        <button className="cancel-btn" onClick={closeModal}>H·ªßy</button>
                    </div>
                </div>
            </DraggablePopup>
        </>
    );

};

export default ChangePasswordModal;
