import React, { useEffect, useState } from "react";
import axios from "axios";

const Notifications = () => {
    const [notifications, setNotifications] = useState([]);
    const [newNotification, setNewNotification] = useState({ title: "", message: "", recipient: "all" });

    useEffect(() => {
        fetchNotifications();
    }, []);

    const fetchNotifications = async () => {
        try {
            const response = await axios.get("http://localhost:5000/api/notifications");
            setNotifications(response.data);
        } catch (error) {
            console.error("Error fetching notifications:", error);
        }
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/api/notifications/${id}`);
            fetchNotifications();
        } catch (error) {
            console.error("Error deleting notification:", error);
        }
    };

    const handleCreate = async () => {
        try {
            await axios.post("http://localhost:5000/api/notifications", newNotification);
            setNewNotification({ title: "", message: "", recipient: "all" });
            fetchNotifications();
        } catch (error) {
            console.error("Error creating notification:", error);
        }
    };

    return (
        <div>
            <h2>Notifications</h2>
            <div>
                <input
                    type="text"
                    placeholder="Title"
                    value={newNotification.title}
                    onChange={(e) => setNewNotification({ ...newNotification, title: e.target.value })}
                />
                <textarea
                    placeholder="Message"
                    value={newNotification.message}
                    onChange={(e) => setNewNotification({ ...newNotification, message: e.target.value })}
                />
                <button onClick={handleCreate}>Add Notification</button>
            </div>
            <ul>
                {notifications.map((notification) => (
                    <li key={notification.id}>
                        <h4>{notification.title}</h4>
                        <p>{notification.message}</p>
                        <button onClick={() => handleDelete(notification.id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Notifications;
