import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Fees.css";

const Payments = ({ user }) => {
    const navigate = useNavigate();
    const [fees, setFees] = useState([]);
    const [paymentMethod, setPaymentMethod] = useState("bank");

    useEffect(() => {
        if (!user) {
            const storedUser = JSON.parse(localStorage.getItem("user"));
            if (!storedUser) {
                navigate("/login");
                return;
            }
            fetchFees(storedUser.id);
        } else {
            fetchFees(user.id);
        }
    }, [user, navigate]);

    const fetchFees = async (studentId) => {
        try {
            const response = await fetch(`http://localhost:5000/student/fees/${studentId}`);
            if (!response.ok) throw new Error("Lỗi khi tải học phí");
            const data = await response.json();
            setFees(data);
        } catch (error) {
            console.error(error);
            setFees([]);
        }
    };

    const handlePayment = () => {
        alert(`Thanh toán thành công bằng ${paymentMethod === "bank" ? "Chuyển khoản" : "Tiền mặt"}!`);
        // 🛠 TODO: Gửi dữ liệu thanh toán lên server
    };

    return (
        <div className="fees-container">
            <h2>💳 Thanh toán học phí</h2>
            {fees.length === 0 ? (
                <p>Chưa có dữ liệu học phí.</p>
            ) : (
                <>
                    <table className="fees-table">
                        <thead>
                            <tr>
                                <th>📚 Số môn đăng ký</th>
                                <th>💰 Tổng học phí</th>
                                <th>📅 Ngày đóng gần nhất</th>
                                <th>💳 Đã thanh toán</th>
                                <th>💸 Còn lại</th>
                            </tr>
                        </thead>
                        <tbody>
                            {fees.map((fee) => (
                                <tr key={fee.id}>
                                    <td>{fee.subject_count}</td>
                                    <td>{fee.total_fee?.toLocaleString()} VNĐ</td>
                                    <td>{fee.latest_pay_at ? new Date(fee.latest_pay_at).toLocaleDateString() : "Chưa đóng"}</td>
                                    <td>{(fee.already_pay || 0).toLocaleString()} VNĐ</td>
                                    <td>{(fee.remaining || 0).toLocaleString()} VNĐ</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>

                    {/* Khu vực thanh toán */}
                    {fees.some(fee => fee.remaining > 0) && (
                        <div className="payment-section">
                            <h3>🔹 Khu vực thanh toán</h3>
                            <p>Tổng số tiền cần thanh toán: <strong>{fees.reduce((sum, fee) => sum + (fee.remaining || 0), 0).toLocaleString()} VNĐ</strong></p>

                            <label>Chọn phương thức thanh toán:</label>
                            <select value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)}>
                                <option value="bank">💳 Chuyển khoản</option>
                                <option value="cash">💵 Tiền mặt</option>
                            </select>

                            <button className="pay-button" onClick={handlePayment}>Xác nhận thanh toán</button>
                        </div>
                    )}
                    <div className="payment-info">
                        <h3>Phương thức thanh toán</h3>
                        <p>Đóng phí qua QR</p>
                        <p>Cổng thanh toán</p>
                        <p>Ngân hàng hỗ trợ</p>
                        <ol>
                            <li>Sinh viên nộp học phí vào 01 trong 05 ngân hàng: Agribank, HDBank, Sacombank, Vietcombank, BIDV.</li>
                            <li>Khuyến khích chuyển khoản từ bất kỳ ngân hàng nào vào tài khoản của Trường Đại học Cần Thơ.</li>
                            <li>Nội dung chuyển khoản theo cú pháp: <strong>MSSV – Họ tên sv – số điện thoại</strong></li>
                        </ol>
                        <h4>Thông tin tài khoản ngân hàng:</h4>
                        <ul>
                            <li>Agribank: 1800 2010 03948</li>
                            <li>HDBank: 0077 0407 0018 367</li>
                            <li>Sacombank: 0700 6111 3779</li>
                            <li>Vietcombank: 0111 00031 8806</li>
                            <li>BIDV: 7410 6 12345</li>
                        </ul>
                        <p><strong>Hạn chót đóng học phí: 02/03/2025</strong></p>
                        <p>Sau thời hạn đóng học phí, sinh viên không đóng học phí sẽ bị hủy kết quả học tập...</p>
                    </div>
                </>
            )}
        </div>
    );
};

export default Payments;
