import { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { io } from "socket.io-client";
import { FaHome, FaBell, FaCog, FaUser, FaSignOutAlt } from "react-icons/fa";
import "./Home.css";
import axios from "axios";

const socket = io("http://localhost:5000"); // K·∫øt n·ªëi t·ªõi server WebSocket

const Home = ({ user, token }) => {
    const navigate = useNavigate();
    const [currentUser, setCurrentUser] = useState(user || JSON.parse(localStorage.getItem("user")));
    const [showSettings, setShowSettings] = useState(false);
    const [notifications, setNotifications] = useState([]);
    const [showNotifications, setShowNotifications] = useState(false);
    const notificationRef = useRef(null);

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        if (!storedUser) {
            navigate("/login");
        } else {
            setCurrentUser(storedUser);
        }

        // L·∫Øng nghe th√¥ng b√°o t·ª´ WebSocket
        socket.emit("join", { userId: storedUser?.id });

        socket.on("newNotification", (notification) => {
            setNotifications((prev) => [notification, ...prev]);
        });

        return () => {
            socket.off("newNotification");
        };
    }, [navigate]);

    // X·ª≠ l√Ω click b√™n ngo√†i ƒë·ªÉ ·∫©n th√¥ng b√°o
    useEffect(() => {
        const handleClickOutside = (event) => {
            if (notificationRef.current && !notificationRef.current.contains(event.target)) {
                setShowNotifications(false);
            }
        };

        document.addEventListener("mousedown", handleClickOutside);
        return () => document.removeEventListener("mousedown", handleClickOutside);
    }, []);

    const handleLogout = () => {
        localStorage.removeItem("user");
        setCurrentUser(null);
        navigate("/login");
    };

    if (!currentUser) return <p>ƒêang t·∫£i...</p>;

    return (
        <div className="home-container">
            <nav className="navbar">
                <h1>Trung T√¢m Gia S∆∞</h1>
                <div className="navbar-buttons">
                    <span className="nav-item" onClick={() => navigate("/home")} title="Trang ch·ªß">
                        <FaHome />
                    </span>

                    {/* Th√¥ng b√°o */}
                    <div className="relative" ref={notificationRef}>
                        <span
                            className="nav-item relative"
                            onClick={() => setShowNotifications(!showNotifications)}
                            title="Th√¥ng b√°o"
                        >
                            <FaBell />
                            {notifications.length > 0 && (
                                <span className="notification-badge">{notifications.length}</span>
                            )}
                        </span>
                        {showNotifications && (
                            <div className="dropdown-menu">
                                <h3 className="dropdown-title">Th√¥ng b√°o</h3>
                                <ul className="dropdown-list">
                                    {notifications.length === 0 ? (
                                        <li className="empty-notif">Kh√¥ng c√≥ th√¥ng b√°o</li>
                                    ) : (
                                        notifications.map((notif, index) => (
                                            <li key={index} className="notif-item">
                                                <p><strong>{notif.user}</strong> {notif.message}</p>
                                                <span className="notif-time">{notif.time}</span>
                                            </li>
                                        ))
                                    )}
                                </ul>
                                <button className="view-all" onClick={() => navigate("/notifications")}>
                                    Xem t·∫•t c·∫£
                                </button>
                            </div>
                        )}
                    </div>

                    {/* C√†i ƒë·∫∑t */}
                    <div className="settings-container">
                        <span className="nav-item" onClick={() => setShowSettings(!showSettings)} title="C√†i ƒë·∫∑t">
                            <FaCog />
                        </span>
                        {showSettings && (
                            <div className="settings-menu">
                                <span onClick={() => navigate("/settings/profile")}>üîπ Th√¥ng tin c√° nh√¢n</span>
                                <span onClick={() => navigate("/settings/password")}>üîπ ƒê·ªïi m·∫≠t kh·∫©u</span>
                                <span onClick={() => navigate("/settings/preferences")}>üîπ T√πy ch·ªânh giao di·ªán</span>
                            </div>
                        )}
                    </div>

                    {/* H·ªì s∆° */}
                    <span className="nav-item" onClick={() => navigate("/profile")} title="Th√¥ng tin c√° nh√¢n">
                        <FaUser />
                    </span>

                    {/* ƒêƒÉng xu·∫•t */}
                    <span className="nav-item logout" onClick={handleLogout} title="ƒêƒÉng xu·∫•t">
                        <FaSignOutAlt />
                    </span>
                </div>
            </nav>

            <div className="main-content">
                <h2>Ch√†o m·ª´ng, {currentUser.username}!</h2>
            </div>
        </div>
    );
};

export default Home;
