import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Grades.css";

const Grades = ({ user }) => {
    const navigate = useNavigate();
    const [classes, setClasses] = useState([]);  // Danh s√°ch l·ªõp
    const [selectedClass, setSelectedClass] = useState(null);
    const [grades, setGrades] = useState([]);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        if (!user) {
            const storedUser = JSON.parse(localStorage.getItem("user"));
            if (!storedUser) {
                navigate("/login");
                return;
            }
            fetchClasses(storedUser.id);
        } else {
            fetchClasses(user.id);
        }
    }, [user, navigate]);

    const fetchClasses = async (userId) => {
        try {
            const response = await fetch(`http://localhost:5000/student/registered-classes/${userId}`);
            if (!response.ok) throw new Error("L·ªói khi l·∫•y danh s√°ch l·ªõp h·ªçc.");
            const data = await response.json();
            setClasses(data);

            if (data.length > 0) {
                setSelectedClass(data[0]);
                fetchGrades(userId, data[0].id);
            }
        } catch (error) {
            console.error("‚ùå L·ªói:", error);
        }
    };

    const fetchGrades = async (userId, classId) => {
        setLoading(true);
        try {
            const response = await fetch(`http://localhost:5000/student/grades/${userId}/${classId}`);
            if (!response.ok) throw new Error("L·ªói khi l·∫•y ƒëi·ªÉm s·ªë.");
            const data = await response.json();
            setGrades(data);
        } catch (error) {
            console.error("‚ùå L·ªói:", error);
            setGrades([]);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="grades-container">
            <h2>üìö Danh s√°ch l·ªõp h·ªçc</h2>

            {classes.length === 0 ? (
                <p>‚ùå B·∫°n ch∆∞a ƒëƒÉng k√Ω l·ªõp h·ªçc n√†o.</p>
            ) : (
                <table className="class-table">
                    <thead>
                        <tr>
                            <th>M√£ l·ªõp</th>
                            <th>T√™n l·ªõp</th>
                            <th>M√¥n h·ªçc</th>
                            <th>Lo·∫°i</th>
                            <th>Kh·ªëi</th>
                            <th>S·ªë HV t·ªëi ƒëa</th>
                            <th>L·ªãch h·ªçc</th>
                        </tr>
                    </thead>
                    <tbody>
                        {classes.map((cls) => (
                            <tr
                                key={cls.id}
                                className={cls.id === selectedClass?.id ? "selected" : ""}
                                onClick={() => {
                                    setSelectedClass(cls);
                                    fetchGrades(user.id, cls.id);
                                }}
                            >
                                <td>{cls.id}</td>
                                <td>{cls.name}</td>
                                <td>{cls.subject}</td>
                                <td>{cls.type}</td>
                                <td>{cls.grade}</td>
                                <td>{cls.max_student}</td>
                                <td>{cls.schedule}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}

            {selectedClass && (
                <>
                    <h2>üìä ƒêi·ªÉm s·ªë c·ªßa l·ªõp {selectedClass.name}</h2>
                    {loading ? (
                        <p>ƒêang t·∫£i...</p>
                    ) : grades.length === 0 ? (
                        <p>üìå Ch∆∞a c√≥ ƒëi·ªÉm.</p>
                    ) : (
                        <table className="grades-table">
                            <thead>
                                <tr>
                                    <th>üìù T√™n b√†i ki·ªÉm tra</th>
                                    <th>üìä ƒêi·ªÉm s·ªë</th>
                                    <th>üìÖ Ng√†y thi</th>
                                </tr>
                            </thead>
                            <tbody>
                                {grades.map((grade, index) => (
                                    <tr key={index}>
                                        <td>{grade.exam_name}</td>
                                        <td>{grade.score}</td>
                                        <td>{new Date(grade.exam_date).toLocaleDateString()}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    )}
                </>
            )}
        </div>
    );
};

export default Grades;
