import { useState, useEffect } from 'react';

function AttendanceSummary() {
    const [attendance, setAttendance] = useState([]);

    useEffect(() => {
        // Giả lập lấy dữ liệu chấm công từ API
        const mockAttendance = [
            { student: 'Nguyễn Văn A', daysAbsent: 2 },
            { student: 'Trần Thị B', daysAbsent: 0 },
            { student: 'Lê Văn C', daysAbsent: 1 },
            { student: 'Phạm Thị D', daysAbsent: 3 },
        ];
        setAttendance(mockAttendance);
    }, []);

    const generateReport = () => {
        // Tạo báo cáo chấm công
        console.log(attendance);
    };

    return (
        <div>
            <h2>Báo cáo chấm công học sinh</h2>
            <button onClick={generateReport}>Tạo báo cáo</button>
            <table>
                <thead>
                    <tr>
                        <th>Học sinh</th>
                        <th>Số ngày nghỉ</th>
                    </tr>
                </thead>
                <tbody>
                    {attendance.map((item, index) => (
                        <tr key={index}>
                            <td>{item.student}</td>
                            <td>{item.daysAbsent}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default AttendanceSummary;
