import { useState, useEffect } from 'react';

function StudentGrouping() {
    const [students, setStudents] = useState([]);
    const [groupedStudents, setGroupedStudents] = useState([]);

    useEffect(() => {
        // Giả lập lấy dữ liệu học sinh từ API
        const mockStudents = [
            { id: 1, name: 'Nguyễn Văn A', score: 85 },
            { id: 2, name: 'Trần Thị B', score: 75 },
            { id: 3, name: 'Lê Văn C', score: 90 },
            { id: 4, name: 'Phạm Thị D', score: 65 },
        ];
        setStudents(mockStudents);
    }, []);

    const groupStudentsByScore = () => {
        const highScore = students.filter(student => student.score >= 80);
        const mediumScore = students.filter(student => student.score >= 60 && student.score < 80);
        const lowScore = students.filter(student => student.score < 60);

        setGroupedStudents([
            { group: 'High Score', students: highScore },
            { group: 'Medium Score', students: mediumScore },
            { group: 'Low Score', students: lowScore },
        ]);
    };

    return (
        <div>
            <h2>Phân nhóm học sinh theo điểm</h2>
            <button onClick={groupStudentsByScore}>Phân nhóm học sinh</button>
            <div>
                {groupedStudents.length > 0 && (
                    <table>
                        <thead>
                            <tr>
                                <th>Nhóm</th>
                                <th>Học sinh</th>
                            </tr>
                        </thead>
                        <tbody>
                            {groupedStudents.map((group, index) => (
                                <tr key={index}>
                                    <td>{group.group}</td>
                                    <td>
                                        {group.students.map(student => (
                                            <div key={student.id}>{student.name}</div>
                                        ))}
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
            </div>
        </div>
    );
}

export default StudentGrouping;
