import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Payments.css';

const Payments = ({ studentId }) => {
    const [fees, setFees] = useState([]);
    const [message, setMessage] = useState('');

    // Tải danh sách học phí từ backend
    useEffect(() => {
        fetchFees();
    }, []);

    const fetchFees = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/student/fees/${studentId}`);
            setFees(response.data);
        } catch (error) {
            console.error('Lỗi khi tải học phí:', error);
            setMessage('Có lỗi xảy ra khi tải dữ liệu!');
        }
    };

    // Xử lý thanh toán
    const handlePayment = async () => {
        if (window.confirm('Bạn có chắc chắn muốn thanh toán học phí?')) {
            try {
                const response = await axios.post(`http://localhost:5000/student/pay`, { studentId });
                if (response.data.success) {
                    setMessage('Thanh toán thành công!');
                    fetchFees(); // Cập nhật lại giao diện
                } else {
                    setMessage('Thanh toán thất bại!');
                }
            } catch (error) {
                console.error('Lỗi thanh toán:', error);
                setMessage('Có lỗi xảy ra khi thanh toán!');
            }
        }
    };

    // Kiểm tra xem có môn nào chưa thanh toán không
    const hasUnpaid = fees.some(fee => !fee.is_paid);

    return (
        <div className="fee-container">
            <h2>Thanh toán học phí</h2>
            <table className="fee-table">
                <thead>
                    <tr>
                        <th>Tên môn học</th>
                        <th>Học phí</th>
                        <th>Ngày bắt đầu</th>
                        <th>Ngày kết thúc</th>
                        <th>Số tiền</th>
                        <th>Trạng thái</th>
                    </tr>
                </thead>
                <tbody>
                    {fees.map(fee => (
                        <tr key={fee.id}>
                            <td>{fee.name}</td>
                            <td>{fee.fee.toLocaleString('vi-VN')} VNĐ</td>
                            <td>{fee.start_at}</td>
                            <td>{fee.end_at}</td>
                            <td>{fee.amount.toLocaleString('vi-VN')} VNĐ</td>
                            <td className={fee.is_paid ? 'paid' : 'unpaid'}>
                                {fee.is_paid ? 'Đã thanh toán' : 'Chưa thanh toán'}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button
                className={`pay-button ${!hasUnpaid ? 'disabled' : ''}`}
                onClick={handlePayment}
                disabled={!hasUnpaid}
            >
                Xác nhận thanh toán
            </button>
            {message && <div className="message">{message}</div>}
        </div>
    );
};

export default Payments;
