import React from "react";
import Slider from "react-slick";

const ImageSlider = () => {
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 3000,
        arrows: true,
    };

    const images = [
        "https://source.unsplash.com/1200x600/?education",
        "https://source.unsplash.com/1200x600/?students",
        "https://source.unsplash.com/1200x600/?books"
    ];

    return (
        <div style={{ width: "80%", margin: "auto", paddingTop: "20px" }}>
            <Slider {...settings}>
                {images.map((img, index) => (
                    <div key={index}>
                        <img src={img} alt={`Slide ${index + 1}`} />
                    </div>
                ))}
            </Slider>
        </div>
    );
};

export default ImageSlider;
