import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { FaHome, FaBell, FaCog, FaUser, FaSignOutAlt } from "react-icons/fa";
import "./Home.css";

const Home = ({ user }) => {
    const navigate = useNavigate();
    const [currentUser, setCurrentUser] = useState(user || JSON.parse(localStorage.getItem("user")));
    const [showSettings, setShowSettings] = useState(false);

    useEffect(() => {
        if (!currentUser) {
            navigate("/login");
        }
    }, [currentUser, navigate]);

    const handleLogout = () => {
        localStorage.removeItem("user");
        setCurrentUser(null);
        navigate("/login");
    };

    return (
        <div className="home-container">
            <nav className="navbar">
                <h1>Trung Tâm Gia Sư</h1>
                <div className="navbar-buttons">
                    <span className="nav-item" onClick={() => navigate("/home")}><FaHome /> Trang chủ</span>
                    <span className="nav-item" onClick={() => navigate("/notifications")}><FaBell /> Thông báo</span>
                    <div className="settings-container">
                        <span className="nav-item" onClick={() => setShowSettings(!showSettings)}>
                            <FaCog /> Cài đặt
                        </span>
                        {showSettings && (
                            <div className="settings-menu">
                                <span onClick={() => navigate("/settings/profile")}>🔹 Thông tin cá nhân</span>
                                <span onClick={() => navigate("/settings/password")}>🔹 Đổi mật khẩu</span>
                                <span onClick={() => navigate("/settings/preferences")}>🔹 Tùy chỉnh giao diện</span>
                            </div>
                        )}
                    </div>
                    {currentUser ? (
                        <>
                            <span className="nav-item" onClick={() => navigate("/profile")}><FaUser /> Thông tin cá nhân</span>
                            <span className="nav-item logout" onClick={handleLogout}><FaSignOutAlt /> Đăng xuất</span>
                        </>
                    ) : (
                        <>
                            <span className="nav-item" onClick={() => navigate("/login")}><FaUser /> Đăng nhập</span>
                            <span className="nav-item" onClick={() => navigate("/register")}><FaUser /> Đăng ký</span>
                        </>
                    )}
                </div>
            </nav>
            {currentUser && (
                <div className="main-content">
                    <aside className="sidebar">
                        <ul>
                            <li className="sidebar-item" onClick={() => navigate("/home")}><FaHome /> Trang chủ</li>
                            {sidebarItems[currentUser.role]?.map((item, index) => (
                                <li key={index} className="sidebar-item" onClick={() => navigate(item.path)}>
                                    {item.icon} {item.label}
                                </li>
                            ))}
                        </ul>
                    </aside>
                    <main className="dashboard">
                        <section className="dashboard-content">
                            <h2>Bảng điều khiển</h2>
                            <p>
                                📌 {
                                    {
                                        hv: "Bạn có thể xem lịch học, điểm số và học phí tại đây.",
                                        gv: "Bạn có thể xem lịch giảng dạy, nhập điểm và chấm công.",
                                        cm: "Quản lý nhóm học, báo cáo tài chính, giáo viên, học viên, lịch học, học phí & lương, báo cáo & thống kê, thông báo & tin tức.",
                                        ad: "Quản lý hệ thống, lập báo cáo doanh thu & lương, theo dõi điểm danh.",
                                    }[currentUser.role]
                                }
                            </p>
                        </section>
                    </main>
                </div>
            )}
        </div>
    );
};

export default Home;
