import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const RegisterGroup = () => {
    const navigate = useNavigate();
    const [classes, setClasses] = useState([]);
    const currentUser = JSON.parse(localStorage.getItem("user"));

    useEffect(() => {
        if (!currentUser) {
            navigate("/login");
        } else {
            fetchClasses();
        }
    }, [navigate]);

    const fetchClasses = async () => {
        try {
            const res = await axios.get("http://localhost:5000/student/classes");
            console.log("üìå D·ªØ li·ªáu t·ª´ API:", res.data); // Debug
            const groupedClasses = groupByClass(res.data);
            setClasses(groupedClasses);
        } catch (err) {
            console.error("‚ùå L·ªói t·∫£i danh s√°ch l·ªõp h·ªçc:", err);
        }
    };


    const handleRegister = async (classId) => {
        try {
            const response = await axios.post(
                "http://localhost:5000/student/register-group",
                { userId: currentUser.id, classId },
                { headers: { Authorization: `Bearer ${currentUser.token}` } }
            );
            alert(response.data.message);
            fetchClasses();
        } catch (error) {
            console.error("‚ùå L·ªói ƒëƒÉng k√Ω l·ªõp h·ªçc:", error);
            alert("ƒêƒÉng k√Ω th·∫•t b·∫°i!");
        }
    };

    // H√†m nh√≥m l·ªãch h·ªçc theo t·ª´ng l·ªõp
    const groupByClass = (data) => {
        const grouped = {};
        data.forEach((item) => {
            if (!grouped[item.id]) {
                grouped[item.id] = {
                    id: item.id,
                    name: item.name,
                    type: item.type,
                    grade: item.grade,
                    max_student: item.max_student,
                    schedule: [],
                };
            }
            grouped[item.id].schedule.push({
                date_of_week: item.date_of_week,
                start_at: item.start_at,
                end_at: item.end_at,
            });
        });
        return Object.values(grouped);
    };

    return (
        <div className="max-w-4xl mx-auto p-6 bg-white rounded-xl shadow-lg">
            <h2 className="text-2xl font-bold text-gray-800 mb-4">ƒêƒÉng k√Ω l·ªõp h·ªçc</h2>
            <div className="overflow-x-auto">
                <table className="w-full table-auto border-collapse border border-gray-200">
                    <thead>
                        <tr className="bg-gray-100">
                            <th className="border p-2 text-left">T√™n l·ªõp</th>
                            <th className="border p-2 text-left">Lo·∫°i</th>
                            <th className="border p-2 text-left">Kh·ªëi</th>
                            <th className="border p-2 text-left">S·ªë HV t·ªëi ƒëa</th>
                            <th className="border p-2 text-left">L·ªãch h·ªçc</th>
                            <th className="border p-2 text-center">ƒêƒÉng k√Ω</th>
                        </tr>
                    </thead>
                    <tbody>
                        {classes.length > 0 ? (
                            classes.map((cls) => (
                                <tr key={cls.id} className="hover:bg-gray-50">
                                    <td className="border p-2">{cls.name}</td>
                                    <td className="border p-2">{cls.type}</td>
                                    <td className="border p-2">{cls.grade}</td>
                                    <td className="border p-2">{cls.max_student}</td>
                                    <td className="border p-2">
                                        <ul className="list-disc pl-4">
                                            {cls.schedule.map((sch, index) => (
                                                <li key={index}>
                                                    {sch.date_of_week}: {sch.start_at} - {sch.end_at}
                                                </li>
                                            ))}
                                        </ul>
                                    </td>
                                    <td className="border p-2 text-center">
                                        <button
                                            onClick={() => handleRegister(cls.id)}
                                            className="px-3 py-1 bg-blue-500 text-white rounded hover:bg-blue-600"
                                        >
                                            ƒêƒÉng k√Ω
                                        </button>
                                    </td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan="6" className="border p-4 text-center text-gray-500">
                                    Kh√¥ng c√≥ l·ªõp h·ªçc n√†o.
                                </td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
            <button
                onClick={() => navigate("/")}
                className="mt-4 px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600"
            >
                Quay l·∫°i
            </button>
        </div>
    );
};

export default RegisterGroup;
