import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const RegisterGroup = () => {
    const navigate = useNavigate();
    const [groups, setGroups] = useState([]);
    const currentUser = JSON.parse(localStorage.getItem("user"));

    useEffect(() => {
        if (!currentUser) {
            navigate("/login");
        } else {
            fetchGroups();
        }
    }, [navigate]);

    const fetchGroups = async () => {
        try {
            const res = await axios.get("http://localhost:5000/api/groups");
            setGroups(res.data);
        } catch (err) {
            console.error("Lỗi tải nhóm học:", err);
        }
    };

    const handleRegister = async (groupId) => {
        try {
            const response = await axios.post(
                "http://localhost:5000/api/register-group",
                { userId: currentUser.id, groupId },
                { headers: { Authorization: `Bearer ${currentUser.token}` } }
            );
            alert(response.data.message);
            fetchGroups();
        } catch (error) {
            console.error("Lỗi đăng ký nhóm:", error);
            alert("Đăng ký thất bại!");
        }
    };

    return (
        <div className="max-w-4xl mx-auto p-6 bg-white rounded-xl shadow-lg">
            <h2 className="text-2xl font-bold text-gray-800 mb-4">Đăng ký nhóm học</h2>
            <div className="overflow-x-auto">
                <table className="w-full table-auto border-collapse border border-gray-200">
                    <thead>
                        <tr className="bg-gray-100">
                            <th className="border p-2 text-left">Tên nhóm</th>
                            <th className="border p-2 text-left">Giáo viên</th>
                            <th className="border p-2 text-left">Lịch học</th>
                            <th className="border p-2 text-center">Đăng ký</th>
                        </tr>
                    </thead>
                    <tbody>
                        {groups.length > 0 ? (
                            groups.map((group) => (
                                <tr key={group.id} className="hover:bg-gray-50">
                                    <td className="border p-2">{group.name}</td>
                                    <td className="border p-2">{group.teacher}</td>
                                    <td className="border p-2">
                                        <ul className="list-disc pl-4">
                                            {group.schedule.map((item, index) => (
                                                <li key={index}>
                                                    {item.day_of_week}: {item.start_time} - {item.end_time}
                                                </li>
                                            ))}
                                        </ul>
                                    </td>
                                    <td className="border p-2 text-center">
                                        <button
                                            onClick={() => handleRegister(group.id)}
                                            className="px-3 py-1 bg-blue-500 text-white rounded hover:bg-blue-600"
                                        >
                                            Đăng ký
                                        </button>
                                    </td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan="4" className="border p-4 text-center text-gray-500">
                                    Không có nhóm học nào.
                                </td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
            <button
                onClick={() => navigate("/")}
                className="mt-4 px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600"
            >
                Quay lại
            </button>
        </div>
    );
};

export default RegisterGroup;
