import { useEffect, useState } from "react";
import { QRCodeCanvas } from "qrcode.react";
import axios from "axios";

const Payments = ({ studentId }) => {
    const [fees, setFees] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        axios.get(`/api/fees/${studentId}`)
            .then(response => {
                setFees(response.data);
                setLoading(false);
            })
            .catch(err => {
                setError("Lỗi khi lấy thông tin học phí!");
                setLoading(false);
            });
    }, [studentId]);

    const handlePayment = async () => {
        try {
            await axios.post("/api/payment", { studentId });
            alert("Thanh toán thành công!");
            window.location.reload();
        } catch (err) {
            alert("Thanh toán thất bại!");
        }
    };

    if (loading) return <p>Đang tải...</p>;
    if (error) return <p>{error}</p>;

    return (
        <div>
            <h2>Học phí</h2>
            <p>Đã thanh toán: {fees.already_pay} VND</p>
            <p>Cần thanh toán: {fees.already_unpaid} VND</p>

            {fees.already_unpaid > 0 && (
                <div>
                    <h3>Quét QR để thanh toán</h3>
                    <QRCode value={`pay?amount=${fees.already_unpaid}&studentId=${studentId}`} size={200} />
                    <button onClick={handlePayment}>Xác nhận đã thanh toán</button>
                </div>
            )}
        </div>
    );
};

export default Payments;
