import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import DraggablePopup from "./DraggablePopup";
import "./Profile.css";

const Profile = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [updatedInfo, setUpdatedInfo] = useState({ fullName: "", email: "", phone: "" });
    const [message, setMessage] = useState(null);

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        if (!storedUser) {
            navigate("/login");
        } else {
            setUser(storedUser);
            setUpdatedInfo({ fullName: storedUser.fullName, email: storedUser.email, phone: storedUser.phone || "" });
        }
    }, [navigate]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUpdatedInfo({ ...updatedInfo, [name]: value });
    };

    const handleSaveChanges = async () => {
        try {
            const response = await fetch("http://localhost:5000/student/update-profile", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ id: user.id, ...updatedInfo })
            });
            const data = await response.json();
            if (response.ok) {
                setMessage("Cập nhật thông tin thành công!");
                localStorage.setItem("user", JSON.stringify({ ...user, ...updatedInfo }));
                setUser({ ...user, ...updatedInfo });
                setIsEditing(false);
            } else {
                setMessage(data.message || "Cập nhật thất bại!");
            }
        } catch (error) {
            setMessage("Lỗi kết nối đến máy chủ!");
        }
    };

    if (!user) {
        return <p>Đang tải...</p>;
    }

    return (
        <div className="profile-container">
            <h2>👤 Thông tin cá nhân</h2>
            <div className="profile-card">
                {isEditing ? (
                    <>
                        <label>Họ và tên:</label>
                        <input type="text" name="fullName" value={updatedInfo.fullName} onChange={handleInputChange} />
                        <label>Email:</label>
                        <input type="email" name="email" value={updatedInfo.email} onChange={handleInputChange} />
                        <label>Số điện thoại:</label>
                        <input type="text" name="phone" value={updatedInfo.phone} onChange={handleInputChange} />
                        <button onClick={handleSaveChanges} className="save-btn">💾 Lưu thay đổi</button>
                        <button onClick={() => setIsEditing(false)} className="cancel-btn">❌ Hủy</button>
                    </>
                ) : (
                    <>
                        <p><strong>👤 Họ và tên:</strong> {user.fullName}</p>
                        <p><strong>📧 Email:</strong> {user.email}</p>
                        <p><strong>📞 Số điện thoại:</strong> {user.phone || "Chưa cập nhật"}</p>
                        <p><strong>🎭 Vai trò:</strong> {user.role.toUpperCase()}</p>
                        <button onClick={() => setIsEditing(true)} className="edit-btn">📝 Chỉnh sửa</button>
                    </>
                )}
            </div>
            {message && <p className="message">{message}</p>}
            <button onClick={() => navigate("/home")} className="back-btn">⬅️ Quay lại</button>
        </div>
    );
};

export default Profile;
