import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Home.css";

const Home = ({ user }) => {
    const navigate = useNavigate();
    const [currentUser, setCurrentUser] = useState(user || JSON.parse(localStorage.getItem("user")));

    useEffect(() => {
        if (!currentUser) {
            navigate("/login");
        }
    }, [currentUser, navigate]);

    const handleLogout = () => {
        localStorage.removeItem("user");
        setCurrentUser(null);
        navigate("/login");
    };

    if (!currentUser) return <p>Đang tải...</p>;

    const sidebarItems = {
        hv: [
            { path: "/schedule", label: "📖 Lịch học" },
            { path: "/grades", label: "📊 Điểm số" },
            { path: "/fees", label: "💰 Học phí" },
            { path: "/makeup-classes", label: "🔄 Học bù" },
        ],
        gv: [
            { path: "/teaching-schedule", label: "📚 Lịch giảng dạy" },
            { path: "/student-grades-input", label: "📝 Chấm điểm học sinh" },
            { path: "/attendance", label: "⏳ Chấm công" },
        ],
        cm: [
            { path: "/manage-groups", label: "📖 Quản lý nhóm học" },
            { path: "/finance-reports", label: "📊 Báo cáo tài chính" },
            { path: "/manage-teachers", label: "📋 Quản lý giáo viên" },
            { path: "/manage-students", label: "📜 Quản lý học viên" },
            { path: "/manage-schedules", label: "🕒 Quản lý lịch học" },
            { path: "/tuition-salary", label: "💰 Quản lý học phí & lương" },
            { path: "/reports-statistics", label: "📈 Báo cáo & Thống kê" },
            { path: "/notifications", label: "🔔 Quản lý thông báo & tin tức" },
        ],
        ad: [
            { path: "/system-management", label: "⚙️ Quản lý hệ thống" },
            { path: "/revenue-salary", label: "📊 Doanh thu & lương" },
            { path: "/attendance-reports", label: "📑 Báo cáo chuyên cần" },
        ],
    };

    return (
        <div className="home-container">
            <nav className="navbar">
                <h1>Trung Tâm Gia Sư</h1>
                <div className="navbar-buttons">
                    {window.location.pathname !== "/home" && <span className="nav-item" onClick={() => navigate("/home")}>🏠 Trang chủ</span>}
                    <span className="nav-item" onClick={() => navigate("/notifications")}>🔔 Thông báo</span>
                    <span className="nav-item" onClick={() => navigate("/settings")}>⚙️ Cài đặt</span>
                    <span className="nav-item" onClick={() => navigate("/profile")}>👤 Thông tin cá nhân</span>
                    <span className="nav-item" onClick={handleLogout}>🚪 Đăng xuất</span>
                </div>
            </nav>

            <div className="main-content">
                <aside className="sidebar">
                    <div className="sidebar-container">
                        <ul>
                            <li className="sidebar-item" onClick={() => navigate("/home")}>🏠 Trang chủ</li>
                            {sidebarItems[currentUser.role]?.map((item, index) => (
                                <li key={index} className="sidebar-item" onClick={() => navigate(item.path)}>
                                    {item.label}
                                </li>
                            ))}
                        </ul>
                    </div>
                </aside>

                <main className="dashboard">
                    <section className="dashboard-content">
                        <h2>Bảng điều khiển</h2>
                        <p>
                            📌 {
                                {
                                    hv: "Bạn có thể xem lịch học, điểm số và học phí tại đây.",
                                    gv: "Bạn có thể xem lịch giảng dạy, nhập điểm và chấm công.",
                                    cm: "Quản lý nhóm học, báo cáo tài chính, giáo viên, học viên, lịch học, học phí & lương, báo cáo & thống kê, thông báo & tin tức.",
                                    ad: "Quản lý hệ thống, lập báo cáo doanh thu & lương, theo dõi điểm danh.",
                                }[currentUser.role]
                            }
                        </p>
                    </section>
                </main>
            </div>
        </div>
    );
};

export default Home;
