import React, { useState, useEffect } from "react";
import axios from "axios";

const TeacherGradeInput = () => {
    const [students, setStudents] = useState([]);
    const [grades, setGrades] = useState({}); // Lưu điểm theo từng học viên

    // Lấy danh sách học viên
    useEffect(() => {
        axios.get("http://localhost:5000/student/user")
            .then(res => setStudents(res.data))
            .catch(err => console.error("Lỗi khi lấy danh sách học viên:", err));
    }, []);

    // Cập nhật điểm khi nhập vào ô input
    const handleScoreChange = (studentId, value) => {
        setGrades(prev => ({ ...prev, [studentId]: value }));
    };

    // Gửi điểm lên server
    const handleSubmit = (studentId) => {
        const score = grades[studentId];
        if (!score) {
            alert("Vui lòng nhập điểm trước khi xác nhận");
            return;
        }

        axios.post("http://localhost:5000/student/grades", {
            student_id: studentId,
            exam_name: "Kiểm tra định kỳ", // Giả định tên bài kiểm tra
            score: parseFloat(score),
            exam_date: new Date().toISOString().split("T")[0] // Lấy ngày hiện tại
        }).then(() => {
            alert("Nhập điểm thành công!");
            setGrades(prev => ({ ...prev, [studentId]: "" })); // Xóa điểm sau khi nhập
        }).catch(err => console.error("Lỗi khi nhập điểm:", err));
    };

    return (
        <div>
            <h2>Nhập Điểm Cho Học Viên</h2>
            <table border="1">
                <thead>
                    <tr>
                        <th>Họ và tên</th>
                        <th>Điểm</th>
                        <th>Hành động</th>
                    </tr>
                </thead>
                <tbody>
                    {students.map(student => (
                        <tr key={student.id}>
                            <td>{student.fullName}</td>
                            <td>
                                <input
                                    type="number"
                                    placeholder="Nhập điểm"
                                    value={grades[student.id] || ""}
                                    onChange={(e) => handleScoreChange(student.id, e.target.value)}
                                />
                            </td>
                            <td>
                                <button onClick={() => handleSubmit(student.id)}>Xác nhận</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default TeacherGradeInput;
