import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Home = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);

    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem("user"));

        if (!storedUser) {
            navigate("/login");
        } else {
            setUser(storedUser);
        }
    }, [navigate]);

    if (!user) {
        return <p>Đang tải...</p>;
    }

    return (
        <div className="home-container">
            <h1>Xin chào, {user.username}!</h1>
            <h2>Vai trò: {user.role === "hv" ? "Học viên" :
                user.role === "gv" ? "Giáo viên" :
                    user.role === "cm" ? "Quản lý trung tâm" :
                        "Admin"}</h2>

            {user.role === "ad" && (
                <div>
                    <h3>🔹 Admin Dashboard</h3>
                    <ul>
                        <li>📌 Quản lý toàn bộ trung tâm</li>
                        <li>📊 Xem báo cáo doanh thu, lương giáo viên</li>
                        <li>⚙️ Quản lý tài khoản người dùng</li>
                    </ul>
                </div>
            )}

            {user.role === "cm" && (
                <div>
                    <h3>📋 Quản lý Trung Tâm</h3>
                    <ul>
                        <li>📖 Quản lý nhóm học</li>
                        <li>🗓️ Xem & chỉnh sửa thời khóa biểu</li>
                        <li>📊 Xem báo cáo tài chính & chuyên cần</li>
                    </ul>
                </div>
            )}

            {user.role === "gv" && (
                <div>
                    <h3>📝 Giáo viên</h3>
                    <ul>
                        <li>📚 Xem lịch giảng dạy</li>
                        <li>📝 Chấm điểm học sinh</li>
                        <li>⏳ Chấm công đầu buổi</li>
                    </ul>
                </div>
            )}

            {user.role === "hv" && (
                <div>
                    <h3>🎓 Học viên</h3>
                    <ul>
                        <li>📖 Xem lịch học</li>
                        <li>📊 Xem điểm số</li>
                        <li>💰 Đóng học phí</li>
                        <li>🔄 Học bù nếu bận</li>
                    </ul>
                </div>
            )}
        </div>
    );
};

export default Home;
