import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Home = ({ user }) => {  // ✅ Nhận user từ props
    const navigate = useNavigate();
    const [currentUser, setCurrentUser] = useState(user);

    useEffect(() => {
        if (!user) {
            const storedUser = JSON.parse(localStorage.getItem("user"));
            if (!storedUser) {
                navigate("/login");
            } else {
                setCurrentUser(storedUser);
            }
        }
    }, [user, navigate]);

    if (!currentUser) {
        return <p>Đang tải...</p>;
    }

    return (
        <div className="home-container">
            <h1>Xin chào, {currentUser.username}!</h1>
            <h2>Vai trò: {currentUser.role === "hv" ? "Học viên" :
                currentUser.role === "gv" ? "Giáo viên" :
                    currentUser.role === "cm" ? "Quản lý trung tâm" :
                        "Admin"}</h2>

            {currentUser.role === "ad" && (
                <div>
                    <h3>🔹 Admin Dashboard</h3>
                    <ul>
                        <li>📌 Quản lý toàn bộ trung tâm</li>
                        <li>📊 Xem báo cáo doanh thu, lương giáo viên</li>
                        <li>⚙️ Quản lý tài khoản người dùng</li>
                    </ul>
                </div>
            )}

            {currentUser.role === "cm" && (
                <div>
                    <h3>📋 Quản lý Trung Tâm</h3>
                    <ul>
                        <li>📖 Quản lý nhóm học</li>
                        <li>🗓️ Xem & chỉnh sửa thời khóa biểu</li>
                        <li>📊 Xem báo cáo tài chính & chuyên cần</li>
                    </ul>
                </div>
            )}

            {currentUser.role === "gv" && (
                <div>
                    <h3>📝 Giáo viên</h3>
                    <ul>
                        <li>📚 Xem lịch giảng dạy</li>
                        <li>📝 Chấm điểm học sinh</li>
                        <li>⏳ Chấm công đầu buổi</li>
                    </ul>
                </div>
            )}

            {currentUser.role === "hv" && (
                <div>
                    <h3>🎓 Học viên</h3>
                    <ul>
                        <li>📖 Xem lịch học</li>
                        <li>📊 Xem điểm số</li>
                        <li>💰 Đóng học phí</li>
                        <li>🔄 Học bù nếu bận</li>
                    </ul>
                </div>
            )}
        </div>
    );
};

export default Home;
